                    {
                        AssetDepreciationProfile_ds.object(fieldNum(AssetDepreciationProfile, SmoothDepForTheYearOver95_JP)).enabled(true);
					}
                }

                if (AssetDepreciationProfile.Method == AssetDepreciationMethod::NewStraightLine_JP
                    ||AssetDepreciationProfile.Method == AssetDepreciationMethod::NewDecliningBalance200_JP
                    ||AssetDepreciationProfile.Method == AssetDepreciationMethod::NewDecliningBalance250_JP)
                {
