                }

                if (AssetDepreciationProfile.Method == AssetDepreciationMethod::EquallyDivided_JP)
                {
                    AssetDepreciationProfile_ds.object(fieldNum(AssetDepreciationProfile, NumOfYearsEquallyDivided_JP)).visible(true);
                    AssetDepreciationProfile_ds.object(fieldNum(AssetDepreciationProfile, NumOfYearsEquallyDivided_JP)).mandatory(true);

                    if (AssetDepreciationProfile.RecId)
                    {
                        AssetDepreciationProfile_ds.object(fieldNum(AssetDepreciationProfile, Method)).allowEdit(false);
                    }
                }

                if (AssetDepreciationProfile.orig().Method == AssetDepreciationMethod::EquallyDivided_JP)
                {
                    AssetDepreciationProfile_ds.object(fieldNum(AssetDepreciationProfile, NumOfYearsEquallyDivided_JP)).allowEdit(false);
                }

	            if (AssetDepreciationProfile.Method == AssetDepreciationMethod::SpecialDepreciation_JP)
	            {
	                AssetDepreciationProfile_ds.object(fieldNum(AssetDepreciationProfile, SpecialDepAccountingMethod_JP)).visible(true);
	                AssetDepreciationProfile_ds.object(fieldNum(AssetDepreciationProfile, SpecialDepApplyNumOfPeriods_JP)).visible(true);
	                AssetDepreciationProfile_ds.object(fieldNum(AssetDepreciationProfile, SpecialDepBaseRatio_JP)).visible(true);
	                AssetDepreciationProfile_ds.object(fieldNum(AssetDepreciationProfile, SpecialDepRate_JP)).visible(true);
	                AssetDepreciationProfile_SpecialDepAccountingMethod_JP.helpText("@FixedAssetsIncentiveDepreciation:SYP4880128");
	                AssetDepreciationProfile_SpecialDepRate_JP.label("@FixedAssetsIncentiveDepreciation:SYP4880103");
	                AssetDepreciationProfile_SpecialDepRate_JP.helpText("@FixedAssetsIncentiveDepreciation:SYP4880104");
	            }
	            if (AssetDepreciationProfile.Method == AssetDepreciationMethod::AdditionalDepreciation_JP)
	            {
	                AssetDepreciationProfile_ds.object(fieldNum(AssetDepreciationProfile, SpecialDepAccountingMethod_JP)).visible(true);
	                AssetDepreciationProfile_ds.object(fieldNum(AssetDepreciationProfile, SpecialDepApplyNumOfPeriods_JP)).visible(true);
	                AssetDepreciationProfile_ds.object(fieldNum(AssetDepreciationProfile, SpecialDepRate_JP)).visible(true);
	                AssetDepreciationProfile_SpecialDepAccountingMethod_JP.helpText("@FixedAssetsIncentiveDepreciation:SYP4880244");
	                AssetDepreciationProfile_SpecialDepRate_JP.label("@FixedAssetsIncentiveDepreciation:SYP4880200");
	                AssetDepreciationProfile_SpecialDepRate_JP.helpText("@FixedAssetsIncentiveDepreciation:SYP4880201");
	                if (Fiscal.valueStr() == enum2str(AssetAccrualFiscal::FiscalPeriod))
	                {
	                    AssetDepreciationProfile_SpecialDepAccountingMethod_JP.allowEdit(false);
	                    AssetDepreciationProfile.SpecialDepAccountingMethod_JP = AssetSpecialDepAccountingMethod_JP::DirectOff;
	                }
	            }

                if (AssetDepreciationProfile.Method == AssetDepreciationMethod::ElectronicDepreciation_JP)
                {
                    AssetDepreciationProfile_ds.object(fieldNum(AssetDepreciationProfile, AssetDepRateSchedule_JP)).visible(true);
                    AssetDepreciationProfile_ds.object(fieldNum(AssetDepreciationProfile, SmoothDepForTheYearOver95_JP)).visible(true);
                    if (assetDepreciationProfile.Accrual != AssetAccrual::Yearly)
                    {
                        AssetDepreciationProfile_ds.object(fieldNum(AssetDepreciationProfile, SmoothDepForTheYearOver95_JP)).enabled(true);
                    }
                }
                break;

            case AssetDepreciationMethod::Accelerated_JP:
                interval.visible(false);
                depreciationYear.visible(true);
                if (FeatureStateProvider::isFeatureEnabled(AssetDepCalculation_AcceleratedFeature_JP::instance()))
                {
                    AssetDepreciationProfile_ds.object(fieldNum(AssetDepreciationProfile, DepreciationYear)).allowEdit(true);
                    depreciationYear.allowEdit(true);
                }
                else
                {
                    AssetDepreciationProfile_ds.object(fieldNum(AssetDepreciationProfile, DepreciationYear)).allowEdit(false);
                    depreciationYear.allowEdit(false);
                }
                switchPercentage = false;
                endDependOnServiceLife.visible(false);
                element.setPeriodFrequency();
                calculationBase.visible(false);
                lvpLowValueCost_AU.visible(false);
                lvpLowValueCost_AU.mandatory(false);
                lvpPercentage_AU.visible(false);
                lvpPercentage_AU.mandatory(false);
                lvpFirstYearPercentage_AU.visible(false);
                lvpFirstYearPercentage_AU.mandatory(false);
                break;
            // </GJP>
        }

        // <GIN>
        if (isDepCompanyActEnabled)
        {
            element.updateDesign_IN(switchShift && AssetDepreciationProfile.ShiftDepreciation_IN == NoYes::Yes);
        }
        // </GIN>

        // <GEERU>
        if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoRU]))
        {
            element.updateDesign_RU();
        }
        // </GEERU>
        percentage.visible(switchPercentage);
        percentage.label(percLabel);
        if (!switchPercentage)
        {
            assetDepreciationProfile.Percentage = 0;
        }

        if (lvpLowValueCost_AU.visible() == false)
        {
            assetDepreciationProfile.lvpFirstYearPercentage_AU = 0;
            assetDepreciationProfile.lvpLowValueCost_AU = 0;
            assetDepreciationProfile.lvpPercentage_AU = 0;
        }

        manualSchedules.visible((switchSchedule || switchYearly));

        AssetDepreciationProfile_ds.object(fieldNum(AssetDepreciationProfile, HalfYearDepreciation_AT)).visible(AssetTableDepConv_HalfYearStart::isHalfYearDepreciationMethod_AT(assetDepreciationProfile.Method));
        AssetDepreciationProfile_ds.object(fieldNum(AssetDepreciationProfile, FullYearDepreciation_DE)).visible(assetDepreciationProfile.Method == AssetDepreciationMethod::StraightLineLifeRemaining);

        // update manual schedules grid based on current depreciation profile
        if (switchSchedule || switchYearly)
        {
            QueryBuildDataSource qbds = assetDepreciationProfileSpec_DS.query().dataSourceTable(tablenum(AssetDepreciationProfileSpec));
            QueryBuildRange qbr = qbds.findRange(fieldnum(AssetDepreciationProfileSpec, Profile));

            if (!qbr)
            {
                qbr = qbds.addRange(fieldnum(AssetDepreciationProfileSpec, Profile));
            }
            qbr.value(queryValue(element.currentDepreciationProfile()));

            assetDepreciationProfileSpec_DS.executeQuery();
        }
    }

// <GEERU>
private void updateDesign_RU()
{
    boolean isTaxNonLinearGroup = assetDepreciationProfile.Method == AssetDepreciationMethod::TaxNonLinearGroup_RU;
    assetDepreciationProfile_DS.object(fieldNum(AssetDepreciationProfile, Interval)).visible(!isTaxNonLinearGroup);
    assetDepreciationProfile_DS.object(fieldNum(AssetDepreciationProfile, Accrual)).visible(!isTaxNonLinearGroup);
}

int active()
{
    int ret;

    ret = super();

    element.updateDesign();

    return ret;
}

public void modified()
{
    // <GIN>
    if (isDepCompanyActEnabled && AssetDepreciationProfile.ShiftDepreciation_IN)
    {
        if (Box::yesNo(strFmt("@FixedAssets_ShiftDepreciation:SYP4882905", "@SYS71333"), DialogButton::No) == DialogButton::Yes)
        {
            assetDepreciationProfile.deleteShiftDepreciation_IN();
        }
        else
        {
            assetDepreciationProfile.DepreciationYear = assetDepreciationProfile.orig().DepreciationYear;
        }
    }
    // </GIN>

    super();

    // <GIN>
    element.updateDesign();
    // </GIN>

    element.setPeriodFrequency();
    assetDepreciationProfile_ds.refresh();
}

public void modified()
{
    // <GIN>
    if (isDepCompanyActEnabled && AssetDepreciationProfile.ShiftDepreciation_IN)
    {
        if (Box::yesNo(strFmt("@FixedAssets_ShiftDepreciation:SYP4882905", "@SYS96914"),DialogButton::No) == DialogButton::Yes)
        {
            assetDepreciationProfile.deleteShiftDepreciation_IN();
        }
        else
        {
            AssetDepreciationProfile.Method = AssetDepreciationProfile.orig().Method;
        }
    }
    // </GIN>
    super();

    element.updateDesign();
}

public void modified()
{
    // <GIN>
    if (isDepCompanyActEnabled && !AssetDepreciationProfile.ShiftDepreciation_IN)
    {
        if (Box::yesNo(strFmt("@FixedAssets_ShiftDepreciation:SYP4882905", "@GLS5965"),DialogButton::No) == DialogButton::Yes)
        {
            assetDepreciationProfile.deleteShiftDepreciation_IN();
        }
        else
        {
            AssetDepreciationProfile.ShiftDepreciation_IN = AssetDepreciationProfile.orig().ShiftDepreciation_IN;
        }
    }
    // </GIN>

    super();

    element.updateDesign();
}

public boolean modified()
{
    boolean ret;

    ret = super();

    if (ret)
    {
        element.updateDesign();
    }

    return ret;
}

public boolean modified()
{
    boolean ret;

    ret = super();

    if (ret)
    {
        element.updateDesign();
    }

    return ret;
}
