// <GIN>
protected void makeAssetGroupValModelSetupBonusVisible_IN()
{
    if (isIsoIN)
    {
        if (!AssetParameters::find().DepreciationIncomeTaxAct_IN)
        {
            assetGroupBookSetup_ds.object(fieldNum(AssetGroupBookSetup, AssetGroupDepreciation_IN)).visible(false);
            assetGroupValModelSetupBonus.visible(false);
        }
        else
        {
            assetGroupBookSetup_ds.object(fieldNum(AssetGroupBookSetup, AssetGroupDepreciation_IN)).visible(true);
        }
    }
}

// </GIN>

void checkValModelBook_IN()
{
    boolean assetGroupDepreciationFieldEnabled;
    boolean depreciationFieldEnabled;

    if (assetGroupBookSetup.AssetGroupDepreciation_IN == NoYes::Yes && assetGroupBookSetup.Depreciation == NoYes::No)
    {
        assetGroupDepreciationFieldEnabled = assetGroupBookSetup_ds.object(fieldNum(AssetGroupBookSetup,AssetGroupDepreciation_IN)).enabled(true);
        depreciationFieldEnabled = assetGroupBookSetup_ds.object(fieldNum(AssetGroupBookSetup,Depreciation)).enabled(false);
        assetGroupBookSetup.Depreciation = NoYes::No;
    }
    if (assetGroupBookSetup.Depreciation == NoYes::Yes && assetGroupBookSetup.AssetGroupDepreciation_IN == NoYes::No)
    {
        assetGroupDepreciationFieldEnabled = assetGroupBookSetup_ds.object(fieldNum(AssetGroupBookSetup,Depreciation)).enabled(true);
        depreciationFieldEnabled = assetGroupBookSetup_ds.object(fieldNum(AssetGroupBookSetup,AssetGroupDepreciation_IN)).enabled(false);
        assetGroupBookSetup.AssetGroupDepreciation_IN = NoYes::No;
    }
    if (assetGroupBookSetup.Depreciation == NoYes::No && assetGroupBookSetup.AssetGroupDepreciation_IN == NoYes::No)
    {
        assetGroupDepreciationFieldEnabled = assetGroupBookSetup_ds.object(fieldNum(AssetGroupBookSetup,AssetGroupDepreciation_IN)).enabled(true);
        depreciationFieldEnabled = assetGroupBookSetup_ds.object(fieldNum(AssetGroupBookSetup,Depreciation)).enabled(true);
        element.enableBonusDepreciation_IN();
    }
    if (assetGroupDepreciationFieldEnabled && assetBookTable.CurrentOperationsTax != CurrentOperationsTax::Tax && assetBookTable.CurrentOperationsTax != CurrentOperationsTax::None)
    {
        assetGroupDepreciationFieldEnabled = assetGroupBookSetup_ds.object(fieldNum(AssetGroupBookSetup,AssetGroupDepreciation_IN)).enabled(false);
    }

    if (assetGroupDepreciationFieldEnabled && LedgerJournalTrans_IN_Extension::existLedgerJournalTrans_IN(assetGroupBookSetup.BookId, NoYes::Yes))
    {
        assetGroupBookSetup_ds.object(fieldNum(AssetGroupBookSetup,AssetGroupDepreciation_IN)).enabled(false);
    }
    if (depreciationFieldEnabled && LedgerJournalTrans_IN_Extension::existLedgerJournalTrans_IN(assetGroupBookSetup.BookId, NoYes::No))
    {
        assetGroupBookSetup_ds.object(fieldNum(AssetGroupBookSetup,Depreciation)).enabled(false);
    }
}

void enableBonusDepreciation_IN()
{
    if (isIsoIN)
    {
        if (assetBookTable.CurrentOperationsTax == CurrentOperationsTax::Tax || assetBookTable.CurrentOperationsTax == CurrentOperationsTax::None)
        {
            assetGroupValModelSetupBonus.enabled(AssetParameters::checkAssetParameters_IN());
        }
        else
        {
            assetGroupValModelSetupBonus.enabled(false);
        }
    }
}

    public void init()
    {
        // <GJP><GIN>
        isIsoJP = SysCountryRegionCode::isLegalEntityInCountryRegion([#isoJP]);
        isIsoIN = SysCountryRegionCode::isLegalEntityInCountryRegion([#isoIN]);
        // </GIN></GJP>
        isIsoRU = SysCountryRegionCode::isLegalEntityInCountryRegion([#isoRU]);

        if (!element.args() || (element.args().dataset() != tableNum(AssetGroup) && element.args().dataset() != tableNum(AssetBookTable)))
        {
            throw error(strFmt("@SYS25516",element.name()));
        }

        super();

        // <GIN>
        element.makeAssetGroupValModelSetupBonusVisible_IN();
        // </GIN>

        // <GJP>
        if (AssetParameters::isManuallyChangeDepProfileEnable_JP())
        {
            AssetDepProfileChangeApply_JP.visible(true);
            assetBookTable_ds.object(fieldnum(AssetGroupBookSetup, DepreciationProfile)).visible(false);
        }
		if (isIsoJP)
	    {
	        AssetBookTable_DS.allowEdit(true);
	    }
        // </GJP>

        element.enableControls();
    }

/// <summary>
/// Sets the option to allow editing of certain fields based on the context of the form.
/// </summary>
private void enableControls()
{
    if (element.args().menuItemName() == menuItemDisplayStr(AssetGroupBookSetupBooks))
    {
        AssetGroupBookSetup_ds.object(fieldNum(AssetGroupBookSetup, AssetGroup)).allowEdit(false);
    }

    if (element.args().menuItemName() == menuItemDisplayStr(AssetGroupBookSetupGroup))
    {
        AssetGroupBookSetup_ds.object(fieldNum(AssetGroupBookSetup, BookId)).allowEdit(false);
    }
}

void setPeriodFrequency()
{
    switch (assetDepreciationProfile.DepreciationYear)
    {
        case AssetDepreciationYear::Calendar:
            depreciationCalendar.visible(true);
            depreciationFiscal.visible(false);
            break;
        case AssetDepreciationYear::Fiscal:
            depreciationCalendar.visible(false);
            depreciationFiscal.visible(true);
            break;
    }
}

