private void setFieldsExtSpecialDepreciation_JP(boolean _visible)
{
    boolean     showAllocFields = (_visible && assetDepreciationExtProfile.SpecialDepAccountingMethod_JP == AssetSpecialDepAccountingMethod_JP::Reserve);

    specialDepAccountingMethod_JP.visible(_visible);
    specialDepAllocationPeriods_JP.visible(showAllocFields);
    specialDepAllocationUnit_JP.visible(showAllocFields);
    SpecialDepAllocationConvention_JP.visible(showAllocFields);

    if (showAllocFields && AssetGroupBookSetup.SpecialDepAllocationUnit_JP == AssetAccrualFiscal::Yearly)
    {
        SpecialDepAllocationConvention_JP.allowEdit(false);
    }
    else
    {
        SpecialDepAllocationConvention_JP.allowEdit(true);
    }
}

private void setLifeTimeFieldsState(boolean _enableField, boolean _visableField = true)
{
    // Fields in the Deprecation tab
    depreciation_LifeTime.enabled(_enableField);
    depreciation_LifeTime.allowEdit(_enableField);
    depreciation_LifeTime.skip(!_enableField);

    depreciation_LifeTime.visible(_visableField);
}

private void setServiceLifeFieldsState(boolean _enableField, boolean _visableField = true)
{
    // Fields in the Deprecation tab
    depreciation_ServiceLife.enabled(_enableField);
    depreciation_ServiceLife.allowEdit(_enableField);
    depreciation_ServiceLife.skip(!_enableField);

    depreciation_ServiceLife.visible(_visableField);
}

    void updateDesign()
    {
        element.setFieldsAssetDepreciationProfile();

        if (assetGroupBookSetup.DepreciationAltProfile)
        {
            depreciationAltProfileDependent.visible(true);
            element.setFieldsAssetDepreciationAltProfile();
        }
        else
        {
            depreciationAltProfileDependent.visible(false);
        }

        if (assetGroupBookSetup.DepreciationExtProfile)
        {
            depreciationExtProfileDependent.visible(true);
            element.setFieldsAssetDepreciationExtProfile();
        }
        else
        {
            depreciationExtProfileDependent.visible(false);

            if (isIsoJP)
            {
                depreciationExtMethod.visible(false);
                depreciationExtPercentage.visible(false);
                depreciationExtInterval.visible(false);
                depreciationExtYear.visible(false);
                depreciationExtCalendar.visible(false);
                depreciationExtFiscal.visible(false);
                element.setFieldsExtSpecialDepreciation_JP(false);
            }
        }

        if (assetGroupBookSetup.DepreciationAcceleratedProfile_JP)
	    {
	        DepreciationAccProfileDependent_JP.visible(true);
	    }
	    else
	    {
	        DepreciationAccProfileDependent_JP.visible(false);
	    }

        if (assetGroupBookSetup.checkIfConventionCanBeSet())
        {
            depreciation_depreciationConvention.allowEdit(true);
            depreciation_depreciationConvention.enabled(true);
            depreciation_depreciationConvention.skip(false);
        }
        else
        {
            depreciation_depreciationConvention.allowEdit(false);
            depreciation_depreciationConvention.enabled(false);
            depreciation_depreciationConvention.skip(true);
        }

        // <Remark> Show calcuation base/ Full depreciation field only for manual method </Remark>
        if (assetDepreciationProfile.Method == AssetDepreciationMethod::Manual)
        {
            calculationBase.visible(true);
            endDependOnServiceLife.visible(true);
        }
        else
        {
            calculationBase.visible(false);
            endDependOnServiceLife.visible(false);
        }

        switch (assetDepreciationProfile.Method)
        {
            case AssetDepreciationMethod::LowValuePool:
                element.setServiceLifeFieldsState(false);
                element.setLifeTimeFieldsState(false);
            break;

            case AssetDepreciationMethod::RBSL:
                element.setServiceLifeFieldsState(false, false);
                element.setLifeTimeFieldsState(false);

                depreciation_ServiceLifeYears_FR.visible(true);
                depreciation_ServiceLifeMonths_FR.visible(true);
            break;

            case AssetDepreciationMethod::Consumption:
                element.setServiceLifeFieldsState(false, false);
                element.setLifeTimeFieldsState(false, false);
            break;

            default:
                element.setServiceLifeFieldsState(true);
                element.setLifeTimeFieldsState(true);

                depreciation_ServiceLifeYears_FR.visible(false);
                depreciation_ServiceLifeMonths_FR.visible(false);
            break;
        }

        // <GJP>
        if (isIsoJP)
        {
            if (assetDepreciationProfile.Method == AssetDepreciationMethod::EquallyDivided_JP)
            {
                AssetGroupBookSetup_ds.object(fieldNum(AssetGroupBookSetup, AllowableLimitForAccumulatedDepType_JP)).allowEdit(false);
            }
            else
            {
                AssetGroupBookSetup_ds.object(fieldNum(AssetGroupBookSetup, AllowableLimitForAccumulatedDepType_JP)).allowEdit(true);
            }
        }
        // </GJP>

        // sets the depreciation convention to non-editable in cases where daily accrual profile is selected
        element.setDepreciationConvention();

        if (element.args().dataset() == tableNum(AssetGroup))
        {
            assetGroupBookSetup_GrpId.visible(false);
            assetGroup_Name.visible(false);
        }
        else
        {
            assetGroupBookSetup_BookId.visible(false);
            assetBookTable_Description.visible(false);
        }
    }

private void initDepreciationProfileDataSource(
    QueryBuildDataSource _queryBuildProfile,
    FieldId _assetBookTableProfileFieldId)
{
    QueryBuildDataSource queryBuildProfile = _queryBuildProfile;

    if (queryBuildProfile)
    {
        queryBuildProfile.clearDynalinks();
        queryBuildProfile.addDynalink(
                fieldNum(AssetDepreciationProfile, Profile),
                AssetGroupBookSetup,
                _assetBookTableProfileFieldId);
    }
}

public void initValue()
{
    super();

    AssetBookTable.data(AssetBookTable::find(AssetGroupBookSetup.BookId));
    AssetBookTable_DS.setCurrent();

    AssetGroupBookSetup.DepreciationProfile = AssetBookTable.DepreciationProfile;
    AssetGroupBookSetup.DepreciationAltProfile = AssetBookTable.DepreciationAltProfile;
    AssetGroupBookSetup.DepreciationExtProfile = AssetBookTable.DepreciationExtProfile;
    AssetGroupBookSetup.DepreciationAcceleratedProfile_JP = AssetBookTable.DepreciationAcceleratedProfile_JP;
}

public int active()
{
    int ret;

    ret = super();

    assetGroup.data(AssetGroup::find(assetGroupBookSetup.AssetGroup));
    assetGroup_ds.setCurrent();

    assetBookTable.data(AssetBookTable::find(assetGroupBookSetup.BookId));
    assetBookTable_ds.setCurrent();

    assetDepreciationProfile_old.data(assetDepreciationProfile);
    assetDepreciationAltProfile_old.data(assetDepreciationAltProfile);
    assetDepreciationExtProfile_old.data(assetDepreciationExtProfile);
    assetDepreciationAccProfile_old.data(assetDepreciationAccProfile_JP);

    assetDepreciationProfile.data(assetGroupBookSetup.depreciationProfile());
    assetDepreciationProfile_ds.setCurrent();
    assetDepreciationAltProfile.data(assetGroupBookSetup.depreciationAltProfile());
    assetDepreciationAltProfile_ds.setCurrent();
    assetDepreciationExtProfile.data(assetGroupBookSetup.depreciationExtProfile());
    assetDepreciationExtProfile_ds.setCurrent();
    assetDepreciationAccProfile_JP.data(assetGroupBookSetup.depreciationAcceleratedProfile_JP());
    assetDepreciationAccProfile_JP_ds.setCurrent();

    if (assetDepreciationProfile.Method    !=  assetDepreciationProfile_old.Method ||
       ( assetDepreciationAltProfile      && !assetDepreciationAltProfile_old) ||
       (!assetDepreciationAltProfile      &&  assetDepreciationAltProfile_old) ||
       assetDepreciationAltProfile.Method !=  assetDepreciationAltProfile_old.Method ||
       ( assetDepreciationExtProfile      && !assetDepreciationExtProfile_old) ||
       (!assetDepreciationExtProfile      &&  assetDepreciationExtProfile_old) ||
       assetDepreciationExtProfile.Method !=  assetDepreciationExtProfile_old.Method ||
       // <GJP>
       assetDepreciationAccProfile_JP.Method !=  assetDepreciationAccProfile_old.Method ||
       ( assetDepreciationAccProfile_JP     && !assetDepreciationAccProfile_old) ||
       (!assetDepreciationAccProfile_JP     &&  assetDepreciationAccProfile_old))
       // </GJP>
    {
        element.updateDesign();
    }

    // <GIN>
    if (AssetParameters::checkAssetParameters_IN())
    {
        element.checkValModelBook_IN();
    }
    // </GIN>
    // <GJP>
    if (AssetParameters::isManuallyChangeDepProfileEnable_JP())
    {
        AssetDepProfileChangeApply_JP.enabled(
            AssetDepProfileChangeHelper_JP::isDepProfileChangeEnabled(AssetGroup::find(assetGroupBookSetup.AssetGroup).AssetType, assetGroupBookSetup.DepreciationProfile()));
    }
    // </GJP>

    return ret;
}

public void linkActive()
{
    super();

    element.updateDesign();
}

public void modified()
{
    super();

    assetGroup.data(AssetGroup::find(assetGroupBookSetup.AssetGroup));
    assetGroup_ds.setCurrent();

    element.updateDesign();
}

public void modified()
{
    int     checkStatus = 0;

    super();
    element.checkValModelBook_IN();
    if (assetBookTable.CurrentOperationsTax == CurrentOperationsTax::Tax)
    {
        checkStatus = assetGroupBookSetup.checkAssetGroupDepreciation_IN();
        if (checkStatus == 1 || assetGroupBookSetup.Depreciation == NoYes::Yes)
        {
            assetGroupBookSetup.AssetGroupDepreciation_IN = NoYes::No;
            assetGroupBookSetup_ds.object(fieldNum(AssetGroupBookSetup,Depreciation)).enabled(true);
            assetGroupBookSetup_ds.object(fieldNum(AssetGroupBookSetup,AssetGroupDepreciation_IN)).enabled(false);
        }
    }
}

public void modified()
{
    super();

    assetBookTable.data(AssetBookTable::find(assetGroupBookSetup.BookId));
    assetBookTable_ds.setCurrent();

    assetDepreciationProfile_ds.executeQuery();
    assetDepreciationAltProfile_ds.executeQuery();
    assetDepreciationExtProfile_ds.executeQuery();
    AssetDepreciationAccProfile_JP_ds.executeQuery();
    element.updateDesign();
    // <GIN>
    if (AssetParameters::checkAssetParameters_IN())
    {
        element.checkValModelBook_IN();
    }
    // </GIN>
}

public void modified()
{
    // <GIN>
    #COSNumbers
    int    checkStatus = 0;
    // </GIN>
    super();

    // <GIN>
    if (AssetParameters::checkAssetParameters_IN())
    {
        element.checkValModelBook_IN();
    }

    if (assetBookTable.CurrentOperationsTax == CurrentOperationsTax::Tax)
    {
        checkStatus = assetGroupBookSetup.checkAssetGroupDepreciation_IN();
        if (checkStatus == #2 || assetGroupBookSetup.AssetGroupDepreciation_IN == NoYes::Yes)
        {
            assetGroupBookSetup.Depreciation = NoYes::No;
            assetGroupBookSetup_ds.object(fieldNum(AssetGroupBookSetup,Depreciation)).enabled(false);
            assetGroupBookSetup_ds.object(fieldNum(AssetGroupBookSetup,AssetGroupDepreciation_IN)).enabled(AssetParameters::checkAssetParameters_IN());
        }
    }
    // </GIN>
}

public void modified()
{
    super();

    assetDepreciationExtProfile_ds.executeQuery();

    if (assetGroupBookSetup_ds.validateWrite())
    {
        assetGroupBookSetup_ds.write();
        assetDepreciationExtProfile_old.data(assetDepreciationExtProfile);
        assetDepreciationExtProfile_ds.research();
        assetDepreciationExtProfile.data(assetGroupBookSetup.depreciationExtProfile());
        assetDepreciationExtProfile_ds.setCurrent();

        element.updateDesign();
    }
}

