public boolean canClose()
{
    boolean ret;
    AssetImpairmentCGUGroup_JP cguGroup;

    ret = super();

    if (ret)
    {
        while select cguGroup
        where cguGroup.Status == AssetImpairmentCGUGroupStatus_JP::Draft
        {
            if (!(element.priorityCheck(cguGroup) && cguGroup.priorityCheck()))
            {
                ret = false;
                break;
            }
        }
    }

    return ret;
}

/// <summary>
/// Callback method to copy from another CGU.
/// </summary>
/// <param name="_sourceCGUName">
/// The source CGU name.
/// </param>
public void copyCGUCallback(AssetImpairmentCGUName_JP _sourceCGUName)
{
    assetImpairmentCGU_JP.copyFromOtherCGU(_sourceCGUName);

    // Database was updated during upper call, so need to refresh dataSource here.
    assetImpairmentCGU_JP_ds.reread();
    assetImpairmentCGU_JP_ds.refresh();
}

