 /// <summary>
 /// Check the priority setup of a CGU group.
 /// </summary>
 /// <param name="_group">
 /// The CGU group
 /// </param>
 /// <returns>
 /// If a goodwill has a higher priority.
 /// </returns>
 public boolean priorityCheck(AssetImpairmentCGUGroup_JP _group)
 {
     AssetImpairmentCGUSharedAssets_JP   cguSharedAssets;
     AssetImpairmentCGUSharedAssets_JP   nonGoodWillCGUSharedAssets;
     int                                 priorityGoodwill;
     int                                 prioritySharedAssets;
     Map                                 mapLoc = new Map(Types::Integer, Types::Enum);
     boolean                             ret = true;

     // Priority check will only occur for Method I
     if (_group.SharedAssetImpairMethod == AssetImpairmentSharedAssetsMethod_JP::Method1)

     {
         select firstOnly RecId from nonGoodWillCGUSharedAssets
                 where nonGoodWillCGUSharedAssets.cguGroup           == _group.RecId
                     && nonGoodWillCGUSharedAssets.SharedAssetType   != AssetImpairmentSharedAssetType_JP::Goodwill;

         // If there is no other fixed assets but goodwill left only, then skip below check.
         if (nonGoodWillCGUSharedAssets.RecId)

         {
             select maxof(AllocationPriority) from cguSharedAssets
                         where cguSharedAssets.cguGroup          == _group.RecId
                             && cguSharedAssets.SharedAssetType  == AssetImpairmentSharedAssetType_JP::Goodwill;
             priorityGoodwill = cguSharedAssets.AllocationPriority;

             select minof(AllocationPriority) from cguSharedAssets
                         where cguSharedAssets.cguGroup          == _group.RecId
                             && cguSharedAssets.SharedAssetType  == AssetImpairmentSharedAssetType_JP::SharedAsset;
             prioritySharedAssets = cguSharedAssets.AllocationPriority;

             // Checks if a goodwill has a higher priority.
             if (priorityGoodwill > prioritySharedAssets)
             {
                 if (!Box::confirm(strFmt("@FixedAssetsImpairementAccounting:SYP4880607", "@FixedAssetsImpairementAccounting:SYP4880233", _group.Name, "@FixedAssetsImpairementAccounting:SYP4880231")))
                 {
                     return false;
                 }
             }
         }
     }

     return ret;
}

