/// <summary>
/// Update the enabled property base on group status
/// </summary>
/// <param name = "_status">Asset impairment CGU group status</param>
public void updateButtonStatus(AssetImpairmentCGUGroupStatus_JP _status)
{
    ActivateButton.enabled(_status == AssetImpairmentCGUGroupStatus_JP::Draft);
    DeactivateButton.enabled(_status== AssetImpairmentCGUGroupStatus_JP::Active);
    DisableButton.enabled(_status!= AssetImpairmentCGUGroupStatus_JP::Disabled);
}

/// <summary>
/// Changes the CGU group status
/// </summary>
public void changeCGUGroupStatus(AssetImpairmentCGUGroupStatus_JP _status)
{
    if (assetImpairmentCGUGroup_JP.Status != _status)
    {
        assetImpairmentCGUGroup_JP.Status = _status;
    }
    if (assetImpairmentCGUGroup_JP_DS.object(fieldNum(AssetImpairmentCGUGroup_JP, Status)).validate())
    {
        assetImpairmentCGUGroup_JP_DS.object(fieldNum(AssetImpairmentCGUGroup_JP, Status)).modified();
    }
}

public void create(boolean _append = false)
{
    super(_append);

    assetImpairmentCGUSharedAssets_JP.BookId = AssetParameters::find().BookIdDefault;
}

public int active()
{
    int ret;

    ret = super();

    if (assetImpairmentCGUSharedAssets_JP.SharedAssetType == AssetImpairmentSharedAssetType_JP::Goodwill)
    {
        assetImpairmentCGUSharedAssets_JP.NetSellingPrice = 0;
        element.enableNetSellingPrice(false);
    }
    else
    {
        element.enableNetSellingPrice(true);
    }

    return ret;
}

public void init()
{
    super();

    if (assetImpairmentCGUGroup_JP.Status != AssetImpairmentCGUGroupStatus_JP::Draft)
    {
        assetImpairmentCGUSharedAssets_JP_DS.cacheAddMethod(tableMethodStr(AssetImpairmentCGUSharedAssets_JP, displayFixedAssetGroup), false);
        assetImpairmentCGUSharedAssets_JP_DS.cacheAddMethod(tableMethodStr(AssetImpairmentCGUSharedAssets_JP, displayFixedAssetName), false);
        assetImpairmentCGUSharedAssets_JP_DS.cacheAddMethod(tableMethodStr(AssetImpairmentCGUSharedAssets_JP, displayFixedAssetStatus), false);
    }
}

public void modified()
{
    super();

    if (assetImpairmentCGUSharedAssets_JP.AssetId)
    {
        assetImpairmentCGUSharedAssets_JP.BookId = AssetBook::getTopDefaultBookIdForAssetId(assetImpairmentCGUSharedAssets_JP.AssetId, curext(), AssetImpairmentCGUGroup_JP.PostingLayer);

        if (AssetBook::find(assetImpairmentCGUSharedAssets_JP.AssetId, assetImpairmentCGUSharedAssets_JP.BookId).AssetFairValue)
        {
            assetImpairmentCGUSharedAssets_JP.NetSellingPrice   = AssetBook::find(assetImpairmentCGUSharedAssets_JP.AssetId, assetImpairmentCGUSharedAssets_JP.BookId).AssetFairValue;
        }
    }
    else
    {
        assetImpairmentCGUSharedAssets_JP.BookId = '';
    }

    assetImpairmentCGUSharedAssets_JP.SharedAssetType = assetImpairmentCGUSharedAssets_JP.defaultAssetType();

    assetImpairmentCGUSharedAssets_JP.initPriority();
}

public boolean validate()
{
    boolean ret;

    ret = super();

    if (ret && AssetImpairmentCGUSharedAssets_JP.BookId)
    {
        ret = assetBookMergeCtrl.validateBookId(AssetImpairmentCGUSharedAssets_JP.AssetId, AssetImpairmentCGUSharedAssets_JP.BookId, AssetImpairmentCGUGroup_JP.PostingLayer);
    }

    return ret;
}

public void modified()
{
    super();

    if (assetImpairmentCGUSharedAssets_JP.SharedAssetType == AssetImpairmentSharedAssetType_JP::Goodwill)
    {
        assetImpairmentCGUSharedAssets_JP.NetSellingPrice = 0;
        assetImpairmentCGUSharedAssets_JP.AllocationPriority = 1;
        element.enableNetSellingPrice(false);
    }
    else
    {
        element.enableNetSellingPrice(true);
    }
}

public void init()
{
    super();
    cguGroup_JP_SharedAssetImpairMethod.text('');
}

public void selectionChanged()
{
    super();

    switch (assetImpairmentCGUGroup_JP.Status)
    {
        case AssetImpairmentCGUGroupStatus_JP::Disabled:
            element.setStatusDisable();
            break;

        case AssetImpairmentCGUGroupStatus_JP::Active:
            element.setStatusActive();
            break;

        case AssetImpairmentCGUGroupStatus_JP::Draft:
            element.setStatusDraft();
            break;
    }

    assetImpairmentCGUSharedAssets_JP_AllocationPriority.visible(assetImpairmentCGUGroup_JP.SharedAssetImpairMethod == AssetImpairmentSharedAssetsMethod_JP::Method1);
}

public void initValue()
{
    super();
    AssetImpairmentCGUGroup_JP.SharedAssetHasHigherPriority = AssetImpairmentSharedAssetPriority_JP::HigherThanOrdinaryAsset;
}

public void modified()
{
    super();

    assetImpairmentCGUGroup_JP_DS.object(fieldNum(AssetImpairmentCGUGroup_JP, SharedAssetHasHigherPriority)).allowEdit(assetImpairmentCGUGroup_JP.SharedAssetImpairMethod == AssetImpairmentSharedAssetsMethod_JP::Method2);
    if (AssetImpairmentCGUGroup_JP.SharedAssetImpairMethod == AssetImpairmentSharedAssetsMethod_JP::Method1)
    {
        AssetImpairmentCGUGroup_JP.SharedAssetHasHigherPriority = AssetImpairmentSharedAssetPriority_JP::SameWithOrdinaryAsset;
    }
}

    public void modified()
    {
        AssetImpairmentCGUGroupStatus_JP origStatus = assetImpairmentCGUGroup_JP.orig().Status;
        AssetImpairmentCGUGroupStatus_JP newStatus;

        super();

        newStatus = assetImpairmentCGUGroup_JP.Status;

        if (origStatus == AssetImpairmentCGUGroupStatus_JP::Draft
&& newStatus == AssetImpairmentCGUGroupStatus_JP::Active)
        {
            element.setStatusActive();
            assetImpairmentCGUGroup_JP.write();
        }
        else if (origStatus == AssetImpairmentCGUGroupStatus_JP::Active
     && newStatus == AssetImpairmentCGUGroupStatus_JP::Draft)
        {
            element.setStatusDraft();
            assetImpairmentCGUGroup_JP.write();
        }
        else if ((origStatus == AssetImpairmentCGUGroupStatus_JP::Draft
      || origStatus == AssetImpairmentCGUGroupStatus_JP::Active)
     && newStatus == AssetImpairmentCGUGroupStatus_JP::Disabled)
        {
            element.setStatusDisable();
        }
    }

    public boolean validate()
    {
        boolean                             ret;
        AssetImpairmentTestResult_JP        testResult;
        AssetImpairmentCGUGroupStatus_JP    origStatus = AssetImpairmentCGUGroup_JP.orig().Status;
        AssetImpairmentCGUGroupStatus_JP    newStatus = AssetImpairmentCGUGroup_JP.Status;

        select firstOnly testResult
where testResult.CGUGroup   == AssetImpairmentCGUGroup_JP.RecId
&&    testResult.Status     != AssetImpairmentTestResultStatus_JP::Posted
&&    testResult.Status     != AssetImpairmentTestResultStatus_JP::Discarded;

        ret = super();

        if (ret && origStatus != newStatus)
        {
            if (origStatus == AssetImpairmentCGUGroupStatus_JP::Draft
    && newStatus == AssetImpairmentCGUGroupStatus_JP::Active)
            {
                // You are going to activate the CGU group. You will be able to use it in Recognition test and calculation of impairment. However, you can no longer edit any data under the CGU group except undiscounted cash flow and  recoverable value of cash generating units, and net selling price of shared assets. \nDo you want to proceed?
                ret = Box::confirm(strFmtLB("@FixedAssetsImpairementAccounting:SYP4880237"));

                ret = ret && element.priorityCheck(AssetImpairmentCGUGroup_JP);
                ret = ret && AssetImpairmentCGUGroup_JP.holdingEntryCheck();
                ret = ret && AssetImpairmentCGUGroup_JP.priorityCheck();

                if (ret && AssetImpairmentCGUGroup_JP.SharedAssetImpairMethod == AssetImpairmentSharedAssetsMethod_JP::Method2)
                {
                    ret = ret && AssetImpairmentCGUGroup_JP.isSharedAssetFullyAllocated();
                }
            }
            else if (origStatus == AssetImpairmentCGUGroupStatus_JP::Active
         && newStatus == AssetImpairmentCGUGroupStatus_JP::Draft)
            {
                if (Box::confirm(strFmtLB("@FixedAssetsImpairementAccounting:SYP4880238")))
                {
                    if (testResult)
                    {
                        warning(strFmt("@FixedAssetsImpairementAccounting:SYP4880306", testResult.TestNum));
                        ret = false;
                    }
                    else
                    {
                        ret = true;
                    }
                }
            }
            else if ((origStatus == AssetImpairmentCGUGroupStatus_JP::Draft
          || origStatus == AssetImpairmentCGUGroupStatus_JP::Active)
         && newStatus == AssetImpairmentCGUGroupStatus_JP::Disabled)
            {
                // You are going to disable the CGU group. The CGU group cannot be reactivated or switched to draft once disabled.\nDo you want to proceed?
                ret = Box::confirm(strFmtLB("@FixedAssetsImpairementAccounting:SYP4880239"));
                if (ret)
                {
                    ret = Box::confirm(strFmtLB("@FixedAssetsImpairementAccounting:SYP4880305"));
                }
            }
            else
            {
                ret = false;
            }
        }

        return ret;
    }

public void create(boolean _append = false)
{
    NumberSeq               numberSeq;

    super(_append);

    if (cguNumSeqRefRecId)
    {
        numberSeq = NumberSeq::newGetNumFromId(cguNumSeqRefRecId, true);
        assetImpairmentCGU_JP.Number = numberSeq.num();
    }

    assetImpairmentCGU_JP.ImpairmentAllocBasis = AssetImpairmentCGUAssetAllocBasis_JP::NetBookValue;
    assetImpairmentCGU_JP.UndiscountedCashFlow = 0;
    assetImpairmentCGU_JP.UndiscountedCashFlowLastUpdatedDate = DateTimeUtil::getToday(DateTimeUtil::getUserPreferredTimeZone());
    assetImpairmentCGU_JP.RecoverableAmount = 0;
    assetImpairmentCGU_JP.RecoverableAmtLastUpdatedDate = DateTimeUtil::getToday(DateTimeUtil::getUserPreferredTimeZone());
}

public void delete()
{
    AssetImpairmentCGUName_JP cguNum = assetImpairmentCGU_JP.Number;

    super();

    if (cguNumSeqRefRecId && cguNum)
    {
        NumberSeq::releaseNumber(cguNumSeqRefRecId, cguNum);
    }
}

