void clicked()
{
    Query                               query       = AssetImpairmentAccountingHelper_JP::assetImpairmentQueryBuilderMassiveImport(assetImpairmentCGUGroup_JP.PostingLayer);
    SysQueryRun                         queryRun    = new SysQueryRun(query);

    AssetTable                          assetTable;
    AssetBook                           assetBook;
    AssetImpairmentCGUSharedAssets_JP   cguSharedAssets;

    super();

    if (queryRun.prompt())
    {
        while (queryRun.next())
        {
            cguSharedAssets.clear();
            assetTable = queryRun.get(tableNum(AssetTable));

            assetBook = queryRun.get(tableNum(AssetBook));
            cguSharedAssets.AssetId                 = assetBook.AssetId;
            cguSharedAssets.BookId                  = assetBook.BookId;
            cguSharedAssets.NetSellingPrice         = assetBook.AssetFairValue;

            cguSharedAssets.cguGroup                = assetImpairmentCGUGroup_JP.RecId;
            cguSharedAssets.ProrationMethodOverride = assetImpairmentCGUGroup_JP.SharedAssetAllocBasis;
            cguSharedAssets.SharedAssetType         = assetTable.AssetType == AssetType::Goodwill ? AssetImpairmentSharedAssetType_JP::Goodwill : AssetImpairmentSharedAssetType_JP::SharedAsset;
            cguSharedAssets.initPriority();

            if (cguSharedAssets.validateWrite())
            {
                cguSharedAssets.insert();
            }
        }

        assetImpairmentCGUSharedAssets_JP_ds.executeQuery();
    }
}

public void lookup()
{
    if (assetImpairmentCGUSharedAssets_JP.AssetId)
    {
        assetBookMergeCtrl.assetImpairmentLookupBookId(this, AssetImpairmentCGUGroup_JP.PostingLayer, assetImpairmentCGUSharedAssets_JP.AssetId);
    }
    else
    {
        assetBookMergeCtrl.assetImpairmentLookupBookId(this, AssetImpairmentCGUGroup_JP.PostingLayer);
    }
}
