/// <summary>
/// Callback method to copy from another CGU.
/// </summary>
/// <param name="_sourceCGUName">
/// The source CGU name.
/// </param>
public void copyCGUCallback(AssetImpairmentCGUName_JP _sourceCGUName)
{
    assetImpairmentCGU_JP.copyFromOtherCGU(_sourceCGUName);
    assetImpairmentCGU_JP_DS.reread();
    assetImpairmentCGU_JP_DS.refresh();
    assetImpairmentCGUAssets_JP_DS.executeQuery();
}

/// <summary>
/// Enable/disable the CGU assets datasource.
/// </summary>
/// <param name="_val">
/// Enable or disable.
/// </param>
public void enableCGUAssets(boolean _val)
{
    assetImpairmentCGUAssets_JP_DS.object(fieldNum(AssetImpairmentCGUAssets_JP, AllocationPriority)).allowEdit(true);
    assetImpairmentCGUAssets_JP_DS.object(fieldNum(AssetImpairmentCGUAssets_JP, AssetId)).allowEdit(_val);
    assetImpairmentCGUAssets_JP_DS.object(fieldNum(AssetImpairmentCGUAssets_JP, BookId)).allowEdit(_val);
    assetImpairmentCGUAssets_JP_DS.object(fieldNum(AssetImpairmentCGUAssets_JP, AssetImpairmentAllocationMethod_JP)).allowEdit(_val);
}

/// <summary>
/// Enable/disable the holding asset book fields.
/// </summary>
/// <param name="_val">
/// Enable or disable.
/// </param>
public void enableHoldingAssetBook(boolean _val)
{
    impairmentHoldingFixedAssetId.allowEdit(_val);
    impairmentHoldingFixedAssetId.mandatory(_val);
    impairmentHoldingBookId.allowEdit(_val);
    impairmentHoldingBookId.mandatory(_val);
}

public void init()
{
    AssetImpairmentCGU_JP       cguLoc;
    NumberSequenceReference     numberSeqRef;
    xArgs                       args = element.args();

    if (args != null
    && tableNum(AssetImpairmentCGU_JP) == args.dataset()
    && args.record())
    {
        args.lookupField(fieldNum(AssetImpairmentCGU_JP, RecId));
        cguLoc = args.record() as AssetImpairmentCGU_JP;
        args.lookupValue(int642str(cguLoc.RecId));
    }

    super();

    assetBookMergeCtrl = new AssetBookMergeController_JP();

    numberSeqRef = NumberSeqReference::findReference(extendedTypeNum(AssetImpairmentCGUName_JP));

    if (numberSeqRef.NumberSequenceId)
    {
        cguNumSeqRefRecId = numberSeqRef.NumberSequenceId;
        assetImpairmentCGU_JP_Number.allowEdit(false);
    }
}

