public void selectionChanged()
{
    AssetImpairmentCGUGroup_JP      cguGroup = AssetImpairmentCGUGroup_JP::find(assetImpairmentCGU_JP.cguGroup);
    boolean                         isMethod2 = cguGroup.SharedAssetImpairMethod == AssetImpairmentSharedAssetsMethod_JP::Method2;

    super();

    currentMethod = cguGroup.SharedAssetImpairMethod;
    sharedAssetsHasHigherPriority = cguGroup.SharedAssetHasHigherPriority == AssetImpairmentSharedAssetPriority_JP::HigherThanOrdinaryAsset;

    switch (cguGroup.Status)
    {
        case AssetImpairmentCGUGroupStatus_JP::Draft:
            element.setStatusDraft();
            break;
        case AssetImpairmentCGUGroupStatus_JP::Active:
            element.setStatusActive();
            break;
        case AssetImpairmentCGUGroupStatus_JP::Disabled:
            element.setStatusDisable();
            break;
    }

    allocationPriority.visible(isMethod2);
    allocationPriority.mandatory(isMethod2);
}

public boolean validateDelete()
{
    AssetImpairmentTestResult_JP        testResult;
    boolean                             ret;

    select firstonly testResult
where testResult.cguGroup   == assetImpairmentCGU_JP.cguGroup;

    if (testResult)
    {
        warning("@FixedAssetsImpairementAccounting:SYP4880322");
        ret = false;
    }
    else
    {
        ret = super();
    }

    return ret;
}

public void create(boolean _append = false)
{
    NumberSeq               numberSeq;

    super(_append);

    if (cguNumSeqRefRecId)
    {
        numberSeq = NumberSeq::newGetNumFromId(cguNumSeqRefRecId, true);
        assetImpairmentCGU_JP.Number = numberSeq.num();
    }

    assetImpairmentCGU_JP.ImpairmentAllocBasis = AssetImpairmentCGUAssetAllocBasis_JP::NetBookValue;
    assetImpairmentCGU_JP.UndiscountedCashFlow = 0;
    assetImpairmentCGU_JP.UndiscountedCashFlowLastUpdatedDate = DateTimeUtil::getToday(DateTimeUtil::getUserPreferredTimeZone());
    assetImpairmentCGU_JP.RecoverableAmount = 0;
    assetImpairmentCGU_JP.RecoverableAmtLastUpdatedDate = DateTimeUtil::getToday(DateTimeUtil::getUserPreferredTimeZone());
}

public void delete()
{
    AssetImpairmentCGUName_JP cguNum = assetImpairmentCGU_JP.Number;

    super();

    if (cguNumSeqRefRecId && cguNum)
    {
        NumberSeq::releaseNumber(cguNumSeqRefRecId, cguNum);
    }
}

public boolean validateWrite()
{
    boolean ret;

    ret = super();

    if (ret && !AssetImpairmentCGU_JP.RecId)
    {
        AssetImpairmentCGUGroup_JP impairmentCGUGroup_JP = AssetImpairmentCGUGroup_JP::find(AssetImpairmentCGU_JP.CGUGroup);

        if (impairmentCGUGroup_JP.Status != AssetImpairmentCGUGroupStatus_JP::Draft)
        {
            ret = checkFailed(strfmt("@FixedAssetsImpairementAccounting:SelectCGUGroupWithStatus", AssetImpairmentCGUGroupStatus_JP::Draft));
        }
    }

    return ret;
}

