    element.enableCGUAssets(assetImpairmentCGUAssets_JP.IsSharedAsset == NoYes::No);
    element.enableHoldingAssetBook(assetImpairmentCGUAssets_JP.AssetImpairmentAllocationMethod_JP == AssetImpairmentAllocationMethod_JP::OtherAsset);

