public void modified()
{
    AssetImpairmentCGUGroup_JP cguGroup;

    super();

    if (assetImpairmentCGUAssets_JP.AssetId)
    {
        cguGroup = AssetImpairmentCGUGroup_JP::find(assetImpairmentCGU_JP.cguGroup);

        assetImpairmentCGUAssets_JP.BookId =
            AssetBook::getTopDefaultBookIdForAssetId(assetImpairmentCGUAssets_JP.AssetId,
                                             curext(),
                                             cguGroup.PostingLayer);
    }
    else
    {
        assetImpairmentCGUAssets_JP.BookId = '';
    }
}

