public boolean validate()
{
    boolean                    ret;
    AssetImpairmentCGUGroup_JP cguGroup;

    ret = super();

    if (ret && AssetImpairmentCGUAssets_JP.BookId)
    {
        cguGroup = AssetImpairmentCGUGroup_JP::find(AssetImpairmentCGU_JP.CGUGroup);

        ret = cguGroup && assetBookMergeCtrl.validateBookId(AssetImpairmentCGUAssets_JP.AssetId, AssetImpairmentCGUAssets_JP.BookId, cguGroup.PostingLayer);
    }
    return ret;
}

    public void modified()
    {
        AssetImpairmentCGUAssets_JP cguAssets;

        super();

        select firstOnly RecId, BookId from cguAssets
where cguAssets.AssetImpairmentCGU == AssetImpairmentCGUAssets_JP.AssetImpairmentCGU
      && cguAssets.RecId != AssetImpairmentCGUAssets_JP.RecId
      && cguAssets.AssetImpairmentAllocationMethod_JP == AssetImpairmentAllocationMethod_JP::Neglected;

        assetImpairmentCGUAssets_JP.ImpairmentHoldingBookId = cguAssets.BookId;
    }

