public void lookup(FormControl _formControl, str _filterStr)
{
    SysTableLookup              d_lookup;
    Query                       d_query = new Query();
    QueryBuildDataSource        qbds;

    d_lookup = SysTableLookup::newParameters(tableNum(AssetImpairmentCGUAssets_JP), _formControl);

    d_lookup.addLookupfield(fieldNum(AssetImpairmentCGUAssets_JP, AssetId));
    qbds = d_query.addDataSource(tableNum(AssetImpairmentCGUAssets_JP));
    qbds.addRange(fieldNum(AssetImpairmentCGUAssets_JP, AssetImpairmentCGU))
.value(queryValue(assetImpairmentCGU_JP.RecId));
    qbds.addRange(fieldNum(AssetImpairmentCGUAssets_JP, AssetImpairmentAllocationMethod_JP))
.value(queryValue(AssetImpairmentAllocationMethod_JP::Neglected));
    qbds.addSortField(fieldNum(AssetImpairmentCGUAssets_JP, AssetId));
    qbds.orderMode(OrderMode::GroupBy);

    d_lookup.parmQuery(d_query);
    d_lookup.performFormLookup();
}

public boolean validate()
{
    boolean                    ret;
    AssetImpairmentCGUGroup_JP cguGroup;

    ret = super();

    if (ret)
    {
        if (assetImpairmentCGUAssets_JP.ImpairmentHoldingAssetId == assetImpairmentCGUAssets_JP.AssetId
&& assetImpairmentCGUAssets_JP.ImpairmentHoldingBookId == assetImpairmentCGUAssets_JP.BookId)
        {
            return checkFailed("@FixedAssetsImpairementAccounting:SYP4880325");
