void clicked()
{
    AssetImpairmentCGUGroup_JP  cguGroup = AssetImpairmentCGUGroup_JP::find(assetImpairmentCGU_JP.cguGroup);
    Query                       query    = AssetImpairmentAccountingHelper_JP::assetImpairmentQueryBuilderMassiveImport(cguGroup.PostingLayer);
    SysQueryRun                 queryRun = new SysQueryRun(query);

    AssetTable                  assetTable;
    AssetBook                   assetBook;
    AssetImpairmentCGUAssets_JP cguAssets;

    super();

    if (queryRun.prompt())
    {
        while (queryRun.next())
        {
            cguAssets.clear();

            assetTable = queryRun.get(tableNum(AssetTable));

            assetBook = queryRun.get(tableNum(AssetBook));
            cguAssets.AssetId                 = assetBook.AssetId;
            cguAssets.BookId                  = assetBook.BookId;

            cguAssets.IsSharedAsset                         = NoYes::No;
            cguAssets.AssetImpairmentCGU                    = assetImpairmentCGU_JP.RecId;
            cguAssets.AssetImpairmentAllocationMethod_JP    = AssetImpairmentAllocationMethod_JP::ThisAsset;
            cguAssets.AllocationPriority = AssetImpairmentCGUAssets_JP::initPriority(currentMethod, sharedAssetsHasHigherPriority);

            if (cguAssets.validateWrite())
            {
                cguAssets.insert();
            }
        }

        assetImpairmentCGUAssets_JP_ds.executeQuery();
    }
}

public void lookup()
{
    AssetImpairmentCGUGroup_JP cguGroup = AssetImpairmentCGUGroup_JP::find(assetImpairmentCGU_JP.cguGroup);

    if (assetImpairmentCGUAssets_JP.AssetId)
    {
        assetBookMergeCtrl.assetImpairmentLookupBookId(this, cguGroup.PostingLayer, assetImpairmentCGUAssets_JP.AssetId);
    }
    else
    {
        assetBookMergeCtrl.assetImpairmentLookupBookId(this, cguGroup.PostingLayer);
    }
}

public int selectionChange()
{
    int ret;

    ret = super();

    if (assetImpairmentCGUAssets_JP.AssetImpairmentAllocationMethod_JP == AssetImpairmentAllocationMethod_JP::OtherAsset)
    {
        element.enableHoldingAssetBook(true);
    }
    else
    {
        element.enableHoldingAssetBook(false);
    }

    return ret;
}
