public void init()
{
    super();

    if (element.args().record())
    {
        AssetImpairmentTestResult_JP_DS.queryBuildDataSource().addRange(fieldNum(AssetImpairmentTestResult_JP, RecId)).value(queryValue(element.args().record().RecId));
    }

    switch (AssetParameters::accountingStandard_JP())
    {
        case AssetAccountingStandard_JP::JPGAAP:
            UndiscDS_UndiscountedCashFlow.visible(true);
            break;
        case AssetAccountingStandard_JP::IFRS:
            UndiscDS_UndiscountedCashFlow.visible(false);
            break;
    }
}
