public class AssetImpairmentRecognition_JP extends FormRun
{
    #MACROLIB.Resource

    AssetImpairmentTestResult_JP    result;
    RefRecId                        subtotalCGURecId;
    RefRecId                        subtotalSharedAssetsRecId;
