public void close()
{
    xArgs arg = element.args();

    super();

    tester.finalize();

    ttsBegin;

    AssetImpairmentTestResult_JP_DS.reread();

    AssetImpairmentTestResult_JP.releaseResult();

    ttsCommit;

    if (arg.caller() && formHasMethod(arg.caller(), identifierStr(updateDataAndDesign)))
    {
        arg.caller().updateDataAndDesign();
    }
}

/// <summary>
/// Enable or disable the step menu items in Function menu.
/// </summary>
/// <param name="_val">
/// Enable or disable.
/// </param>
private void enableStepMenuItems(boolean _val)
{
    ButtonStep1.enabled(_val);
    ButtonStep2.enabled(_val);
    ButtonAllocStep1.enabled(_val);
    ButtonAllocStep2.enabled(_val);
}

public void init()
{
    xArgs arg = element.args();

    if (tableNum(AssetImpairmentTestResult_JP) != arg.dataset() || !arg.record())
    {
        throw error("@FixedAssetsImpairementAccounting:SYP4880546");
    }

    result = arg.record() as AssetImpairmentTestResult_JP;

    if (!result.useResult())
    {
        throw error(strFmt("@FixedAssetsImpairementAccounting:SYP4880526", result.TestNum));
    }

    if (arg.caller() && formHasMethod(arg.caller(), identifierStr(updateDataAndDesign)))
    {
        arg.caller().updateDataAndDesign();
    }

    super();
}

public void run()
{
    xArgs arg = element.args();

    super();

    cguGroup = AssetImpairmentCGUGroup_JP::find(AssetImpairmentTestResult_JP.CGUGroup);

    if (!cguGroup)
    {
        throw error("@FixedAssetsImpairementAccounting:SYP4880492");
    }

    if (cguGroup.SharedAssetImpairMethod == AssetImpairmentSharedAssetsMethod_JP::Method1)
    {
        tester = AssetImpairmentCGUGroupTestMethod1_JP::construct(AssetImpairmentTestResult_JP);
        this.enableStepMenuItems(true);
        Step2TabPage.visible(true);
        GenerateReport.menuItemName(identifierStr(AssetImpairmentAccountingRptMethodI_JP));
        this.design().caption("@FixedAssetsImpairementAccounting:SYP4880565");
    }
    else
    {
        tester = AssetImpairmentCGUGroupTestMethod2_JP::construct(AssetImpairmentTestResult_JP);
        this.enableStepMenuItems(false);
        Step2TabPage.visible(false);
        GenerateReport.menuItemName(identifierStr(AssetImpairmentAccountingRptMethodII_JP));
        Step1TabPage.caption("@FixedAssetsImpairementAccounting:SYP4880629");
        this.design().caption("@FixedAssetsImpairementAccounting:SYP4880600");
    }

    element.updateDataAndDesign();
}

/// <summary>
/// Update data and UI
/// </summary>
public void updateDataAndDesign()
{
    AssetImpairmentTestResultDocument_JP resultDoc;

    element.updateTotalGrid();

    AssetImpairmentTestResult_JP_DS.executeQuery();
    AssetImpairmentTestResultDocument_JP_DS.executeQuery();
    AssetImpairmentRecognitionTmp_JP_DS.executeQuery();

    switch (AssetImpairmentTestResult_JP.Status)
    {
        case AssetImpairmentTestResultStatus_JP::BeforeTest:
            RunTestButton.enabled(true);
            CalculateButton.enabled(false);
            UndiscountedCashFlow.enabled(true);
            RecoverableAmount.enabled(true);
            NotRecoverable.enabled(true);
            PostButton.enabled(false);
            AssetImpairmentTestResult_JP_DS.object(fieldNum(AssetImpairmentTestResult_JP, NetSellingPriceAsRecovable)).allowEdit(true);
            this.enableStepMenuItems(false);
            break;
        case AssetImpairmentTestResultStatus_JP::Tested:
            RunTestButton.enabled(true);
            CalculateButton.enabled(true);
            UndiscountedCashFlow.enabled(true);
            RecoverableAmount.enabled(true);
            NotRecoverable.enabled(true);
            PostButton.enabled(false);
            AssetImpairmentTestResult_JP_DS.object(fieldNum(AssetImpairmentTestResult_JP, NetSellingPriceAsRecovable)).allowEdit(true);
            this.enableStepMenuItems(cguGroup.SharedAssetImpairMethod == AssetImpairmentSharedAssetsMethod_JP::Method1);
            break;
        case AssetImpairmentTestResultStatus_JP::CalcStep1:
        case AssetImpairmentTestResultStatus_JP::CalcStep2:
        case AssetImpairmentTestResultStatus_JP::AllocateStep1:
            RunTestButton.enabled(true);
            CalculateButton.enabled(false);
            PostButton.enabled(false);
            AssetImpairmentTestResult_JP_DS.object(fieldNum(AssetImpairmentTestResult_JP, NetSellingPriceAsRecovable)).allowEdit(true);
            break;
        case AssetImpairmentTestResultStatus_JP::AllocateStep2:
            RunTestButton.enabled(true);
            CalculateButton.enabled(false);
            PostButton.enabled(true);
            AssetImpairmentTestResult_JP_DS.object(fieldNum(AssetImpairmentTestResult_JP, NetSellingPriceAsRecovable)).allowEdit(true);
            break;
        case AssetImpairmentTestResultStatus_JP::ReadyForProposal:
            UndiscountedCashFlow.enabled(false);
            RecoverableAmount.enabled(false);
            NotRecoverable.enabled(false);
            RunTestButton.enabled(false);
            CalculateButton.enabled(false);
            PostButton.enabled(false);
            AssetImpairmentTestResult_JP_DS.object(fieldNum(AssetImpairmentTestResult_JP, NetSellingPriceAsRecovable)).allowEdit(false);
            this.enableStepMenuItems(false);
            break;
        default:
            AssetImpairmentTestResult_JP_DS.allowEdit(false);
            AssetImpairmentTestResultDocument_JP_DS.allowEdit(false);
            AssetImpairmentRecognitionTmp_JP_DS.allowEdit(false);
            RunTestButton.enabled(false);
            CalculateButton.enabled(false);
            PostButton.enabled(false);
            AssetImpairmentTestResult_JP_DS.object(fieldNum(AssetImpairmentTestResult_JP, NetSellingPriceAsRecovable)).allowEdit(false);
            this.enableStepMenuItems(false);
            break;
    }

    SharedAssetsExceeded.visible(tester.parmSharedAssetImpairmentAdjExceeded());
    select firstOnly RecId from resultDoc
        where resultDoc.AssetImpairmentTestResult_JP == AssetImpairmentTestResult_JP.RecId
          && resultDoc.ImpairmentAdjExceeded == true;
    CGUExceeded.visible(resultDoc.RecId);
}

/// <summary>
/// Update values in the total grid for method 1.
/// </summary>
public void updateTotalGrid()
{
    AssetImpairmentRecognitionTmp_JP::setSubtotalCGU(AssetImpairmentRecognitionTmp_JP,
                                                 subtotalCGURecId,
                                                 tester.parmCguSubtotalNbv(),
                                                 tester.parmCguSubtotalUndiscCF(),
                                                 tester.parmCguSubtotalRecovAmt(),
                                                 tester.parmCguSubtotalImpairAdj());
    AssetImpairmentRecognitionTmp_JP::setSubtotalSharedAsstes(AssetImpairmentRecognitionTmp_JP,
                                                          tester.parmSharedAssetImpairmentAdjExceeded(),
                                                          subtotalSharedAssetsRecId,
                                                          tester.parmSharedAssetsSubtotalNbv(),
                                                          tester.parmSharedAssetsSubtotalRecovAmt(),
                                                          tester.parmSharedAssetsSubtotalImpairAdj());
    AssetImpairmentRecognitionTmp_JP::setGrandTotal(AssetImpairmentRecognitionTmp_JP,
                                                grandTotalRecId,
                                                tester.parmGrandTotalNbv(),
                                                tester.parmGrandTotalUndiscCF(),
                                                tester.parmGrandTotalTestResult(),
                                                tester.parmGrandTotalRecovAmt(),
                                                tester.parmGrandTotalImpairAdj());
}

public void modified()
{
    super();
    if (AssetImpairmentTestResult_JP.Status < AssetImpairmentTestResultStatus_JP::ReadyForProposal)
    {
        ttsBegin;
        AssetImpairmentTestResult_JP.Status = AssetImpairmentTestResultStatus_JP::BeforeTest;
        AssetImpairmentTestResult_JP.update();
        ttsCommit;
        element.updateDataAndDesign();
    }
}

public void displayOption(Common _record, FormRowDisplayOption _options)
{
    AssetImpairmentRecognitionTmp_JP loc = _record as AssetImpairmentRecognitionTmp_JP;
    if (loc && loc.RecId == grandTotalRecId)
    {
        _options.fontBold(true);
    }
    super(_record, _options);
}

public void resetGrandTotal()
{
    ttsBegin;

    delete_from AssetImpairmentRecognitionTmp_JP;

    AssetImpairmentRecognitionTmp_JP.initValue();
    AssetImpairmentRecognitionTmp_JP.Item = "@FixedAssetsImpairementAccounting:SYP4880434";
    AssetImpairmentRecognitionTmp_JP.TestResult = NoYesUnchanged::Unchanged;
    AssetImpairmentRecognitionTmp_JP.insert();
    subtotalCGURecId = AssetImpairmentRecognitionTmp_JP.RecId;

    AssetImpairmentRecognitionTmp_JP.clear();
    AssetImpairmentRecognitionTmp_JP.Item = "@FixedAssetsImpairementAccounting:SYP4880435";
    AssetImpairmentRecognitionTmp_JP.TestResult = NoYesUnchanged::Unchanged;
    AssetImpairmentRecognitionTmp_JP.insert();
    subtotalSharedAssetsRecId = AssetImpairmentRecognitionTmp_JP.RecId;

    AssetImpairmentRecognitionTmp_JP.clear();
    AssetImpairmentRecognitionTmp_JP.Item = "@FixedAssetsImpairementAccounting:SYP4880513";
    AssetImpairmentRecognitionTmp_JP.TestResult = NoYesUnchanged::Unchanged;
    AssetImpairmentRecognitionTmp_JP.insert();
    grandTotalRecId = AssetImpairmentRecognitionTmp_JP.RecId;

    ttsCommit;

    AssetImpairmentRecognitionTmp_JP_DS.executeQuery();
}

