public void modified()
{
    super();

    if (AssetImpairmentTestResultDocument_JP.NotRecoverable)
    {
        AssetImpairmentTestResultDocument_JP.RecoverableAmount = 0;
    }

    AssetImpairmentTestResultDocument_JP_DS.object(fieldNum(AssetImpairmentTestResultDocument_JP, RecoverableAmount)).allowEdit(!AssetImpairmentTestResultDocument_JP.NotRecoverable);

    if (AssetImpairmentTestResult_JP.Status > AssetImpairmentTestResultStatus_JP::Tested)
    {
        tester.updateTestStatus(AssetImpairmentTestResultStatus_JP::Tested);
        element.updateDataAndDesign();
    }
}

