void clicked()
{
    if (AssetImpairmentTestResult_JP.Status < AssetImpairmentTestResultStatus_JP::ReadyForProposal)
    {
        tester.updateTestStatus(AssetImpairmentTestResultStatus_JP::BeforeTest);
    }

    tester.recognitionTest();

    element.updateDataAndDesign();

    Step2TabPage.expand();
}

