[Form]
public class AssetImpairmentTestResultEntryList_JP extends FormRun
{
    Map postedAmountCache;
}

        public int active()
{
    int ret;

    ret = super();

    switch (AssetImpairmentTestResultDocEntry_JP.Status)
    {
        case AssetImpairmentTestResultEntryStatus_JP::Posted:
            Discard.enabled(false);
            Reactive.enabled(false);
            break;
        case AssetImpairmentTestResultEntryStatus_JP::NotPosted:
            Discard.enabled(AssetImpairmentTestResult_JP.Status == AssetImpairmentTestResultStatus_JP::ReadyForProposal);
            Reactive.enabled(false);
            break;
        case AssetImpairmentTestResultEntryStatus_JP::Discarded:
            Discard.enabled(false);
            Reactive.enabled(AssetImpairmentTestResult_JP.Status == AssetImpairmentTestResultStatus_JP::ReadyForProposal);
            break;
    }

    return ret;
}

        public void init()
{
    xArgs                        arg = element.args();
    AssetImpairmentTestResult_JP result;

    super();

    if (arg.dataset() != tableNum(AssetImpairmentTestResult_JP)
        || !arg.record())
    {
        throw error("@FixedAssetsImpairementAccounting:SYP4880547");
    }
    else
    {
        result = arg.record() as AssetImpairmentTestResult_JP;
    }

    this.queryBuildDataSource().addRange(fieldNum(AssetImpairmentTestResult_JP, RecId)).value(queryValue(arg.record().RecId));
}

        display AssetAmount displayPostedAmount(AssetDocumentEntry_JP _entry)
{
    if (postedAmountCache == null)
    {
        postedAmountCache = new Map(Types::Int64, Types::Real);
    }

    if (!postedAmountCache.exists(_entry.RecId))
    {
        postedAmountCache.insert(_entry.RecId, AssetImpairmentAccountingHelper_JP::calcPostedImpairmentAmtPerEntry(_entry));
    }

    return postedAmountCache.lookup(_entry.RecId);
}

        void clicked()
{
    AssetImpairmentTestResultDocEntry_JP resultDocEntry;
    AssetImpairmentTestResultDocument_JP resultDoc;
    int                                  i;

    if (!Box::confirm("@FixedAssetsImpairementAccounting:SYP4880447"))
    {
        return;
    }

    ttsBegin;

    if (AssetImpairmentTestResultDocEntry_JP_DS.anyMarked())
    {
        resultDocEntry = AssetImpairmentTestResultDocEntry_JP_DS.getFirst(true);

        while (resultDocEntry)
        {
            if (resultDocEntry.Status == AssetImpairmentTestResultEntryStatus_JP::NotPosted)
            {
                i++;
            }
            resultDocEntry   = AssetImpairmentTestResultDocEntry_JP_DS.getnext();
        }
    }
    else
    {
        i = 1;
    }

    select count(RecId) from resultDocEntry
            where resultDocEntry.Status == AssetImpairmentTestResultEntryStatus_JP::NotPosted
        exists join resultDoc
            where resultDoc.RecId == resultDocEntry.ResultDocument
                    && resultDoc.AssetImpairmentTestResult_JP == AssetImpairmentTestResult_JP.RecId;
    ttsCommit;

    //Do not discard.
    if (i == resultDocEntry.RecId && !Box::confirm("@FixedAssetsImpairementAccounting:SYP4880446"))
    {
        return;
    }

    ttsBegin;

    if (AssetImpairmentTestResultDocEntry_JP_DS.anyMarked())
    {
        resultDocEntry = AssetImpairmentTestResultDocEntry_JP_DS.getFirst(true);

        while (resultDocEntry)
        {
            if (resultDocEntry.Status == AssetImpairmentTestResultEntryStatus_JP::NotPosted)
            {
                resultDocEntry.selectForUpdate(true);
                resultDocEntry.Status = AssetImpairmentTestResultEntryStatus_JP::Discarded;
                resultDocEntry.update();
            }
            resultDocEntry   = AssetImpairmentTestResultDocEntry_JP_DS.getnext();
        }
    }
    else
    {
        AssetImpairmentTestResultDocEntry_JP.Status = AssetImpairmentTestResultEntryStatus_JP::Discarded;
        AssetImpairmentTestResultDocEntry_JP.update();
    }

    if (AssetImpairmentTestResult_JP
        && AssetImpairmentTestResult_JP.Status == AssetImpairmentTestResultStatus_JP::ReadyForProposal)
    {
        select firstOnly RecId from resultDocEntry
            where resultDocEntry.Status == AssetImpairmentTestResultEntryStatus_JP::NotPosted
        exists join resultDoc
            where resultDoc.RecId == resultDocEntry.ResultDocument
                    && resultDoc.AssetImpairmentTestResult_JP == AssetImpairmentTestResult_JP.RecId;

        if (!resultDocEntry)
        {
            AssetImpairmentTestResult_JP.Status = AssetImpairmentTestResultStatus_JP::Posted;
            AssetImpairmentTestResult_JP.update();

            AssetImpairmentTestResult_JP_DS.research();
        }
    }

    ttsCommit;

    AssetImpairmentTestResultDocEntry_JP_DS.research();
}

