    _menuOptions.dataEntityOptions()
        .addEnd(OfficeMenuDataEntityOptions::construct(tableStr(AssetParametersProposalJournalNamesEntity)));
}

/// <summary>
/// Enables or disables the <c>AssetAllowAutoCreate</c> field of the <c>AssetParameters</c> table.
/// </summary>
/// <remarks>
/// The <c>AssetAllowAutoCreate</c> field is enabled when the <c>AssetAllowCreate</c> field is checked.
/// </remarks>
public void enableAssetAllowAutoCreate()
{
    if (assetParameters.AssetAllowCreate)
    {
        assetParameters_ds.object(fieldNum(AssetParameters, AssetAllowAutoCreate)).enabled(true);
    }
    else
    {
        assetParameters_ds.object(fieldNum(AssetParameters, AssetAllowAutoCreate)).enabled(false);
        assetParameters_ds.object(fieldNum(AssetParameters, AssetAllowAutoCreate)).setValue(false);
    }
}

/// <summary>
/// Enables or disables the <c>AssetUserGroupIdPostPO</c> field of the <c>AssetParameters</c> table.
/// </summary>
/// <remarks>
/// The <c>AssetUserGroupIdPostPO</c> field is enabled when the <c>AssetAllowAcqPostingPO</c> field is checked.
/// </remarks>
public void enableAssetUserGroupIdPostPO()
{
    assetParameters_ds.object(fieldNum(AssetParameters, AssetUserGroupIdPostPO)).enabled(assetParameters.AssetAllowAcqPostingPO == NoYes::Yes);
}

void enableDepreciationCompaniesAct_IN()
{
    assetParameters_ds.object(fieldNum(AssetParameters, MinNonSeasonalWorkingDays_IN)).enabled(assetParameters.DepreciationCompaniesAct_IN);
    assetParameters_ds.object(fieldNum(AssetParameters, MinSeasonalWorkingDays_IN)).enabled(assetParameters.DepreciationCompaniesAct_IN);
    assetParameters_ds.object(fieldNum(AssetParameters, MaxAcquisitionValue_IN)).enabled(assetParameters.DepreciationCompaniesAct_IN);

    if (!assetParameters.DepreciationCompaniesAct_IN)
    {
        assetParameters.MinSeasonalWorkingDays_IN       = 0;
        assetParameters.MinNonSeasonalWorkingDays_IN    = 0;
        assetParameters.MaxAcquisitionValue_IN          = 0.00;
    }

    assetParameters_ds.refresh();
}

void enableDepreciationIncomeTaxAct_IN()
{
    assetParameters_ds.object(fieldNum(AssetParameters, ThresholdPercentage_IN)).enabled(assetParameters.DepreciationIncomeTaxAct_IN);
    assetParameters_ds.object(fieldNum(AssetParameters, DepreciationThreshold_IN)).enabled(assetParameters.DepreciationIncomeTaxAct_IN);
    assetParameters_ds.object(fieldNum(AssetParameters, UseSpecialDepreciatonBalance_IN)).enabled(assetParameters.DepreciationIncomeTaxAct_IN);
    assetparameters_ds.object(fieldNum(AssetParameters, AssetSaleBasis_IN)).enabled(AssetParameters.DepreciationIncomeTaxAct_IN);

    if (!assetParameters.DepreciationIncomeTaxAct_IN)
    {
        assetParameters.DepreciationThreshold_IN    = 0;
        assetParameters.ThresholdPercentage_IN      = 0.00;
        assetParameters.UseSpecialDepreciatonBalance_IN = NoYes::No;
    }
    assetParameters_ds.refresh();
}

void init()
{
    this.numberSeqPreInit();

    panel = SysListPanelSet_AssetTransTypeJour::newForm(this, element.controlId(formControlStr(AssetParameters, ReasonCodeTransactions)), enumNum(AssetTransTypeJournal));

    super();

    AssetParameters::find();

    this.numberSeqPostInit();

    panel.init();
    panel.fill();

    reservation_ReserveUnusedAcc.parmPostingType(LedgerPostingType::None);

    // <GEEPL>
    salesInfo_PL_AssetDafaultSaleDimension_PL.parmPostingType(LedgerPostingType::None);
    // </GEEPL>     
    if (Ledger::reportingCurrency() && Ledger::reportingCurrency() != Ledger::accountingCurrency())
    {
        Depreciation_DepreciationMinReportingCurrency.enabled(true);
    }

    LockAssetPosting.visible(true);

    if (FeatureStateProvider::isFeatureEnabled(FixedAssetDisposalSameYearParametersFeature::instance()))
    {
        IsDisposalSameYear.visible(true);
        this.addWhatsNewNotificationForIsDisposalSameYear();
    }
}

private void addWhatsNewNotificationForIsDisposalSameYear()
{
    const str isDisposalSameYearWhatsNewRuleId = '{3D7B3661-5C8B-4CDF-A019-B7E8F0C92D56}';

    SystemNotificationsWhatsNewManager::AddWhatsNewWithActionLink(
            isDisposalSameYearWhatsNewRuleId,
            '',
            "@FixedAssets:FixedAssetDisposalSameYearParametersFeatureSummary",
            IsDisposalSameYear.name(),
            'https://go.microsoft.com/fwlink/?linkid=2272665');
}

public void numberSeqPostInit()
{
    ctrlAllowSameAs.visible(numberSeqApplicationModule.sameAsActive());
    ctrlLabelSameAs.visible(numberSeqApplicationModule.sameAsActive());
}

public void numberSeqPreInit()
{
    runExecuteDirect = false;
    numberSequenceModules = [NumberSeqModule::Asset];
    numberSeqApplicationModule = new NumberSeqModuleAsset();
    scope = NumberSeqScopeFactory::createDataAreaScope();
    NumberSeqApplicationModule::createReferencesMulti(numberSequenceModules, scope);
    tmpIdRef.setTmpData(NumberSequenceReference::configurationKeyTableMulti(numberSequenceModules));
}

void removeFilter()
{
    runExecuteDirect = false;
    numbersequenceReference_ds.executeQuery();
}

void executeQuery()
{
    if (runExecuteDirect)
    {
        super();
    }
    else
    {
        runExecuteDirect = true;
        this.queryRun(NumberSeqReference::buildQueryRunMulti(numberSequenceReference,
                                                             tmpIdRef,
                                                             numberSequenceTable,
                                                             numberSequenceModules,
                                                             scope));
        numbersequenceReference_ds.research();
    }
}

public Common resolveReference(FormReferenceControl _formReferenceControl)
{
    NumberSequenceCode code = _formReferenceControl.filterValue(
        AbsoluteFieldBinding::construct(fieldStr(NumberSequenceTable, NumberSequence),
        tableStr(NumberSequenceTable))).value();

    // Do not call super as we're providing our own disambiguation logic.

    return NumberSequenceTable::findByNaturalKey(code, scope.getId(true));
}

public Common lookupReference(FormReferenceControl _formReferenceControl)
{
    Query lookupQuery;
    NumberSequenceTable selectedRecord;
    SysReferenceTableLookup sysTableLookup = SysReferenceTableLookup::newParameters(tableNum(NumberSequenceTable), _formReferenceControl, true);
    Range currentCompanyScopeRange;
    Range globalScopeRange;
    QueryBuildDataSource qbds;
    NumberSeqScope numberSeqScope;

    numberSeqScope = NumberSeqScopeFactory::createDataAreaScope();
    currentCompanyScopeRange = queryValue(numberSeqScope.getId(true));
    numberSeqScope = NumberSeqScopeFactory::createGlobalScope();
    globalScopeRange = queryValue(numberSeqScope.getId(true));

    // Display the NumberSequence field in the lookup form.
    sysTableLookup.addLookupfield(fieldNum(NumberSequenceTable, NumberSequence));

    // Create a custom Query that filters on NumberSequenceScope.
    lookupQuery = new Query();
    lookupQuery.addDataSource(tableNum(NumberSequenceTable));
    qbds = lookupQuery.dataSourceTable(tableNum(NumberSequenceTable));
    SysQuery::findOrCreateRange(qbds, fieldNum(NumberSequenceTable, NumberSequenceScope)).value(globalScopeRange);
    qbds.addRange(fieldNum(NumberSequenceTable, NumberSequenceScope)).value(currentCompanyScopeRange);
    sysTableLookup.parmQuery(lookupQuery);

    // Return the record selected by the user.
    selectedRecord = sysTableLookup.performFormLookup();

    return selectedRecord;
}

/// <summary>
/// Activates the <c>AssetParameters</c> datasource and:
/// - enables or disables the <c>AssetUserGroupIdPostPO</c> and <c>AssetAllowAutoCreate</c> fields,
/// - enables country specifi logic,
/// - sets default values for maximum books in journal fields.
/// </summary>
/// <returns>
/// Always returns 1.
/// </returns>
int active()
{
    int ret;

    // <GIN>
    #ISOCountryRegionCodes
    // </GIN>

    element.enableAssetUserGroupIdPostPO();
    element.enableAssetAllowAutoCreate();

    ret = super();

    // <GIN>
    if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoIN])
        && (assetParameters.DepreciationCompaniesAct_IN || assetParameters.DepreciationIncomeTaxAct_IN))
    {
        element.enableDepreciationCompaniesAct_IN();
        element.enableDepreciationIncomeTaxAct_IN();
    }
    // </GIN>

    this.initMaxBooksInJournals();

    return ret;
}

private void initMaxBooksInJournals()
{
    if (!assetParameters.MaxBooksInAcquisitionProposalJournal)
    {
        assetParameters.MaxBooksInAcquisitionProposalJournal = FixedAssetsConstants::MaxBooksInAcquisitionProposalJournal;
    }

    if (!assetParameters.MaxBooksInDepreciationProposalJournal)
    {
        assetParameters.MaxBooksInDepreciationProposalJournal = FixedAssetsConstants::MaxBooksInDepreciationProposalJournal;
    }

    if (!assetParameters.MaxBooksInSumDepreciationProposalJournal)
    {
        assetParameters.MaxBooksInSumDepreciationProposalJournal = FixedAssetsConstants::MaxBooksInSumDepreciationProposalJournal;
    }
}

public void write()
{
    super();

    if (Depreciation_DepreciationMinReportingCurrency.enabled()
        && Depreciation_DepreciationMinReportingCurrency.realValue() == 0
        && Depreciation_DepreciationMin.realValue() != 0)
    {
        warning("@FixedAssets:FA_Form_MinDepreciationAmountReportingCurrencyValidation");
    }
}

