/// <summary>
/// Validates if the journal name selected, matches the posting layer specified for the current record.
/// </summary>
/// <returns>True if the journal name matches the posting layer, otherwise false</returns>
public boolean validate()
{
    boolean ret;
    LedgerJournalName ledgerJournalNameLocal;

    ret = super();

    if (ret)
    {
        ret = AssetProposal::isValidJournalNameAndPostingLayer(
                assetParametersProposalJournalNames.JournalName,
                assetParametersProposalJournalNames.PostingType);
    }

    return ret;
}

/// <summary>
/// A custom lookup that displays journals filtered by posting layer.
/// </summary>
/// <param name = "_formControl">Journal field</param>
/// <param name = "_filterStr">Filter value for the lookup</param>
public void lookup(FormControl _formControl, str _filterStr)
{
    SysTableLookup          sysTableLookup;
    QueryBuildDataSource    queryBuildDataSource;
    QueryBuildRange         queryBuildRangeJournalType;
    QueryBuildRange         queryBuildRangePostingLayer;
    Query                   query = new Query();

    sysTableLookup = SysTableLookup::newParameters(tableNum(LedgerJournalName), _formControl);

    sysTableLookup.addLookupfield(fieldNum(LedgerJournalName, JournalName));
    sysTableLookup.addLookupfield(fieldNum(LedgerJournalName, Name));

    queryBuildDataSource = query.addDataSource(tableNum(LedgerJournalName));
    queryBuildRangeJournalType = queryBuildDataSource.addRange(fieldNum(LedgerJournalName, JournalType));
    queryBuildRangePostingLayer = queryBuildDataSource.addRange(fieldNum(LedgerJournalName, CurrentOperationsTax));
    queryBuildRangeJournalType.value(int2str(enum2int(LedgerJournalType::Assets)));

    queryBuildRangePostingLayer.value(enum2str(assetParametersProposalJournalNames.PostingType));
    sysTableLookup.parmQuery(query);

    sysTableLookup.performFormLookup();
}
