private void dataSourceUpdateRangesExecuteQuery(
    FormDataSource _dataSource,
    TableId _tableId,
    FieldId _fieldId,
    anytype _mainType)
{
    QueryBuildDataSource    queryBuildDataSource;
    QueryBuildRange         queryBuildRange;

    queryBuildDataSource = _dataSource.query().dataSourceTable(_tableId);

    queryBuildRange = queryBuildDataSource.findRange(_fieldId);

    if (!queryBuildRange)
    {
        queryBuildRange = queryBuildDataSource.addRange(_fieldId);
    }
    queryBuildRange = queryBuildDataSource.rangeField(_fieldId);
    queryBuildRange.value(queryValue(_mainType));

    _dataSource.executeQuery();
}

public void init()
{
    isIsoRU = SysCountryRegionCode::isLegalEntityInCountryRegion([#isoRU]);
    super();

    // Restrict based on trans type
    // Restrict based on posting profile
    this.initDataSourceRanges(assetLedgerAccounts_ds, tableNum(AssetLedgerAccounts),
        fieldNum(AssetLedgerAccounts, TransType), AssetPost::assetAccountType2AssetTransType(any2Enum(transType.selection())),
        fieldNum(AssetLedgerAccounts, PostingProfile));

    AssetledgerAccounts_AssetLedgerAccount.parmPostingType(LedgerPostingType::None);
    AssetledgerAccounts_AssetLedgerOffsetAccount.parmPostingType(LedgerPostingType::None);

    // Define defaults for AssetDisposalParameters
    this.initAssetParametersDisposalDataSource();

    // <GJP>
    this.initAssetParametersDisposalDataSource_JP();

    if (AssetDocumentUtility_JP::isAssetDocumentEnabled())
    {
        this.initAssetDocumentDataSource_JP();
        isAssetDocumentEnabled = true;
    }
    // </GJP>
    // <GJP><GCN>
    isAssetAllocationEnabled = SysCountryRegionCode::isLegalEntityInCountryRegion([#isoCN, #isoJP]);
    // </GCN></GJP>
}

private void initDataSourceRanges(
    FormDataSource          _sourceDataSource,
    TableId                 _tableId,
    FieldId                 _fieldId,
    anytype                 _fieldValue,
    FieldId                 _profileFieldId)
{
    QueryBuildDataSource    qbds;
    QueryBuildRange         qbr;

    qbds = _sourceDataSource.query().dataSourceTable(_tableId);
    qbr = qbds.findRange(_fieldId);
    if (!qbr)
    {
        qbr = qbds.addRange(_fieldId);
    }
    qbr.value(queryValue(_fieldValue));

    qbr = qbds.findRange(_profileFieldId);
    if (!qbr)
    {
        qbr = qbds.addRange(_profileFieldId);
    }
    qbr.value(queryValue(element.currentPostingProfile()));
}

private void initAssetParametersDisposalDataSource()
{
    AssetDisposalParameters_OffsetAccount.parmPostingType(LedgerPostingType::None);
    AssetDisposalParameters_Account.parmPostingType(LedgerPostingType::None);

    this.initDataSourceRanges(assetDisposalParameters_ds, tableNum(AssetDisposalParameters), fieldNum(AssetDisposalParameters, SoldScrap),
        AssetSoldScrapComboBox.selection(), fieldNum(AssetDisposalParameters, PostingProfile));
}

