protected void setAssetAllocationControls_CN()
{
    if (isAssetAllocationEnabled)
    {
        if (assetLedgerAccounts.allowUseAllocationRules())
        {
            element.setRuleControlLabel_CN(AssetLedgerAccounts.TransType);
            AllocationRules.enabled(true);
        }
        else
        {
            AllocationRules.enabled(false);
        }
    }
}

protected void validateAccountType_IN(AssetAccountType _accountType)
{
    if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoIN])
        && _accountType == AssetAccountType::BonusDepreciation
        && !AssetParameters::find().DepreciationCompaniesAct_IN
        && !AssetParameters::find().DepreciationIncomeTaxAct_IN)
    {
        throw error(strfmt("@GLS6323","@GLS5107","@GLS5109","@SYS94738"));
    }
}

