private void initAssetDocumentDataSource_JP()
{
    //Impairment accounting
    AssetDocImpairLedgerAccounts_JP_LedgerDimension.parmPostingType(LedgerPostingType::None);
    AssetDocImpairLedgerAccounts_JP_OffsetLedgerDimension.parmPostingType(LedgerPostingType::None);

    //Retirement obligation
    AssetROLedgerAccounts_JP_LedgerDimension.parmPostingType(LedgerPostingType::None);
    AssetROLedgerAccounts_JP_OffsetLedgerDimension.parmPostingType(LedgerPostingType::None);

    //Reduction entry
    AssetRELedgerAccounts_JP_LedgerDimension.parmPostingType(LedgerPostingType::None);
    AssetRELedgerAccounts_JP_OffsetLedgerDimension.parmPostingType(LedgerPostingType::None);

    //Accelerated depreciation
    AssetADLedgerAccounts_JP_LedgerDimension.parmPostingType(LedgerPostingType::None);
    AssetADLedgerAccounts_JP_OffsetLedgerDimension.parmPostingType(LedgerPostingType::None);

    this.initAssetDocLedgerAccountsDataSource_JP(ImpairmentAccountType, AssetDocumentLedgerAccountsImpairment_JP_ds, AssetDocumentMainType_JP::AssetImpairment);
    this.initAssetDocLedgerAccountsDataSource_JP(RetirementObligationAccountType, AssetDocumentLedgerAccountsRO_JP_ds, AssetDocumentMainType_JP::AssetRetirementObligation);
    this.initAssetDocLedgerAccountsDataSource_JP(ReductionEntryAccountType, AssetDocumentLedgerAccountsRE_JP_ds, AssetDocumentMainType_JP::ReductionEntry);
    this.initAssetDocLedgerAccountsDataSource_JP(AcceleratedDepreciationAccountType, AssetDocumentLedgerAccountsAD_JP_ds, AssetDocumentMainType_JP::AssetAcceleratedDepreciation);
    }

