public void refreshAssetDocLedgerAccountControls_JP(FormDataSource _dataSource)
{
    AssetDocumentLedgerAccounts_JP assetDocumentLedgerAccountsLocal = _dataSource.cursor();
    _dataSource.object(fieldNum(AssetDocumentLedgerAccounts_JP, AccountRelation)).enabled(assetDocumentLedgerAccountsLocal.AccountCode != TableGroupAll::All);

    if (assetDocumentLedgerAccountsLocal.AccountCode== TableGroupAll::All)
    {
        assetDocumentLedgerAccountsLocal.AccountRelation = '';
    }
}

private void setLookupButton()
{
    assetLedgerAccounts_Num.enabled(assetLedgerAccounts.AccountCode != TableGroupAll::All);

    if (assetLedgerAccounts.AccountCode== TableGroupAll::All)
    {
        assetLedgerAccounts.AccountRelation = '';
    }
    if (isIsoRU)
    {
        this.setLookupButton_RU();
    }
}

private void setLookupButton_RU()
{
    DepreciationGroupId_RU.allowEdit(assetLedgerAccounts.AccountCode != TableGroupAll::Table);
}

private void setDisposalLookupButton()
{
    assetDisposalParameters_AssetRelation.enabled(true);
    assetDisposalParameters_AssetRelation.lookupButton(0);

    if (assetDisposalParameters.AssetCode == TableGroupAll::All)
    {
        assetDisposalParameters_AssetRelation.enabled(false);
        assetDisposalParameters_AssetRelation.lookupButton(1);
        assetDisposalParameters.AssetRelation = '';
    }
    if (isIsoRU)
    {
        this.setDisposalLookupButton_RU();
    }
}

private void setDisposalLookupButton_RU()
{
    DisposalDepreciationGroupId_RU.allowEdit(assetLedgerAccounts.AccountCode != TableGroupAll::Table);
}

