        public int active()
        {
            int                     ret;

            ret = super();

            this.refreshAssetDocPostingProfile(AssetLedgerAccounts_ds, tablenum(AssetLedgerAccounts), fieldnum(AssetLedgerAccounts, PostingProfile));
            this.refreshAssetDocPostingProfile(AssetDisposalParameters_ds, tablenum(AssetDisposalParameters), fieldnum(AssetDisposalParameters, PostingProfile));

            // <GJP>
