                this.refreshAssetDocPostingProfile(AssetDisposalParameters_JP_ds, tablenum(AssetDisposalParameters_JP),
                    fieldnum(AssetDisposalParameters, PostingProfile));
                this.refreshAssetDocPostingProfile(AssetDocumentLedgerAccountsImpairment_JP_ds, tablenum(AssetDocumentLedgerAccounts_JP),
                    fieldnum(AssetDocumentLedgerAccounts_JP, PostingProfile));
                this.refreshAssetDocPostingProfile(AssetDocumentLedgerAccountsRO_JP_ds, tablenum(AssetDocumentLedgerAccounts_JP),
                    fieldnum(AssetDocumentLedgerAccounts_JP, PostingProfile));
                this.refreshAssetDocPostingProfile(AssetDocumentLedgerAccountsRE_JP_ds, tablenum(AssetDocumentLedgerAccounts_JP),
                    fieldnum(AssetDocumentLedgerAccounts_JP, PostingProfile));
				this.refreshAssetDocPostingProfile(AssetDocumentLedgerAccountsAD_JP_ds, tablenum(AssetDocumentLedgerAccounts_JP),
                    fieldnum(AssetDocumentLedgerAccounts_JP, PostingProfile));	
            }
            // </GJP>

            return ret;
        }

