public void lookup()
{
    // <GIN>
    if (AssetParameters::checkAssetParameters_IN())
    {
        if (assetledgerAccounts.TransType == AssetTransType::BonusDepreciation)
        {
            AssetBookTable::valueModelLookup_IN(this,CurrentOperationsTax::Tax);
        }
        else
        {
            AssetBookTable::valueModelLookupAll_IN(this);
        }
    }
    else
    {
    // </GIN>
        super();
    // <GIN>
    }
}

