[Form]
public class AssetProfile extends FormRun
{
    // <GIN>
    MenuItemName                menuItemName;
    int                         selection;
    boolean                     isSpecialDep_IN;
    // </GIN>
    // <GEEPL>
    #isoCountryRegionCodes
    // </GEEPL>
    boolean                     isIsoJP;
    boolean                     isCatchUpMethodUsed_JP;
    boolean                     isEquallyDiviedMethodUsed_JP;
    boolean                     isReductionEntry_JP;
    boolean                     isSpecialDep_JP;
    boolean                     isSpecialDepReserve_JP;
    boolean                     isAcceleratedDep_JP;

}

public void init()
{
    // <GIN>
    menuItemName    = element.args().menuItemName();
    // </GIN>
    // <GJP>
    if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoJP]))
    {
        isIsoJP                         = true;
        isCatchUpMethodUsed_JP          = AssetParameters::find().UseCatchUpMethod_JP;
    }
    // </GJP>

    super();
    // <GIN>
    if (AssetParameters::isDepreciationIncomeTaxActEnable_IN()
        && menuItemName == menuitemDisplayStr(AssetSpecialDepProfile_IN))
    {
        isSpecialDep_IN = true;
        element.design().caption("@FixedAssets_AdditionalDepreciation:SYP4883995");
        TmpDateSum_DS.object(fieldNum(TmpDateSum, TmpNumerals02)).visible(false);
    }
    // </GIN>

    this.makeVisibleReportingCurrencyAmountFields();
}

	private void makeVisibleReportingCurrencyAmountFields()
    {
        boolean isReportingCurrency = Ledger::reportingCurrency() != '';

        tmpDateSum_DS.object(fieldNum(TmpDateSum, TmpNumeralsReportingCurrency01)).visible(isReportingCurrency);
        tmpDateSum_DS.object(fieldNum(TmpDateSum, TmpNumeralsReportingCurrency02)).visible(isReportingCurrency);
        tmpDateSum_DS.object(fieldNum(TmpDateSum, TmpNumeralsReportingCurrency03)).visible(isReportingCurrency);
        tmpDateSum_DS.object(fieldNum(TmpDateSum, TmpNumeralsReportingCurrency04)).visible(isReportingCurrency);
        tmpDateSum_DS.object(fieldNum(TmpDateSum, TotalReportingCurrency)).visible(isReportingCurrency);
    }

void initTmpDateSum()
{
    // <GIN>
    AssetBookTable      assetBookTable;
    AssetDepreciationProfile depreciationProfile;
    AssetDepreciationProfile depreciationAltProfile;
    FiscalCalendar_IN   fiscalCalendar_IN;
    // </GIN>

    // <GIN>
    if (AssetParameters::isDepreciationCompaniesActEnable_IN())
    {
        assetBookTable = AssetBookTable::find(assetBook.BookId);
        fiscalCalendar_IN = FiscalCalendar_IN::findByFiscalCalendar(assetBookTable.FiscalCalendar);
        depreciationProfile = assetBook.depreciationProfile();
        depreciationAltProfile = assetBook.depreciationAltProfile();
    }

    if (AssetParameters::isDepreciationCompaniesActEnable_IN()
        && assetBook.currentOperationsTax()    == CurrentOperationsTax::Current
        && ((depreciationProfile.isShiftDepreciationMethod_IN()
             && depreciationProfile.ShiftDepreciation_IN)
            || (depreciationAltProfile.isShiftDepreciationMethod_IN()
                && depreciationAltProfile.ShiftDepreciation_IN))
        	    && !isSpecialDep_IN)
    {
        typeOfShift.visible(fiscalCalendar_IN.CalendarType == CalendarType_IN::DayBased
                            || depreciationProfile.isNewShiftDepreciationMethod_IN());
    }
    else
    {
        typeOfShift.visible(false);
    }

    if (isSpecialDep_IN)
    {
        tmpDateSum.setTmpData(assetBook.AssetDepBookMap_IN::initSpecialDepreciationProfile());
    }
    else if (AssetParameters::isDepreciationCompaniesActEnable_IN()
        && (assetBookTable.CurrentOperationsTax == CurrentOperationsTax::Current
        && fiscalCalendar_IN.CalendarType    == CalendarType_IN::DayBased
        && assetBook.Depreciation)
        || (assetBookTable.CurrentOperationsTax == CurrentOperationsTax::Tax
        && !assetBook.AssetGroupDepreciation_IN)
        || ((depreciationProfile.isShiftDepreciationMethod_IN()
            && depreciationProfile.ShiftDepreciation_IN)
            || (depreciationAltProfile.isShiftDepreciationMethod_IN()
                && depreciationAltProfile.ShiftDepreciation_IN)))
    {
        tmpDateSum.setTmpData(AssetInitDepreciationProfile_IN::newFromShiftTypeAndBookDate(assetBook, selection).calcDepreciationProfile());
    }
    else if (AssetParameters::checkAssetParameters_IN()
            && assetBook.AssetGroupDepreciation_IN)
    {
        tmpDateSum.setTmpData(tmpDateSum); // For Indian localization if the AssetGroupDepreciation_IN is yes, then its should show the blank value in the standard profile form.
    }
    else
    {
        // </GIN>
        tmpDateSum.setTmpData(AssetInitDepreciationProfile::newFromBookDate(assetBook).calcDepreciationProfile());

        if (isEquallyDiviedMethodUsed_JP)
        {
            AssetEquallyDividedDepProfileTmp_JP.setTmpData(AssetDepCalculationHelper_JP::initEquallyDividedDepProfile(AssetBook));
            AssetEquallyDividedDepProfileTmp_JP_ds.research();
        }
        if (isReductionEntry_JP)
        {
            TmpDateSumReserve_JP.setTmpData(assetBook.initReductionEntryAllocationProfile_JP(tmpDateSum));
        }
        if (isSpecialDepReserve_JP)
        {
            AssetSpecialDepComparisonTmp_JP.setTmpData(AssetDepBookMap_JP::initDepProfileSpecialDepComparison(assetBook));
            AssetSpecialDepComparisonTmp_JP_ds.research();
            AssetSpecialDepReserveTmp_JP.setTmpData(AssetDepBookMap_JP::initDepProfileSpecialDepReserve(assetBook));
            AssetSpecialDepReserveTmp_JP_ds.research();
        }
        // <GIN>
    }
    // </GIN>

    tmpDateSum_DS.research();
}

public void linkActive()
{
    // <GJP>
    AssetDepreciationProfile    assetDepreciationProfile;
    // </GJP>

    super();

    if (assetBook.depreciationAltProfile())
    {
        tmpDateSum_TmpNumerals03.visible(true);
        tmpDateSum_TmpNumerals04.visible(true);
        tmpDateSum_TmpNumerals03ReportingCurrency.visible(true);
        tmpDateSum_TmpNumerals04ReportingCurrency.visible(true);
    }
    else
    {
        tmpDateSum_TmpNumerals03.visible(false);
        tmpDateSum_TmpNumerals04.visible(false);
        tmpDateSum_TmpNumerals03ReportingCurrency.visible(false);
        tmpDateSum_TmpNumerals04ReportingCurrency.visible(false);
    }

    // <GJP>
    if (isCatchUpMethodUsed_JP)
    {
        if (AssetDepreciationProfile::isDepreciationMethodForCatchupMethod_JP(assetBook.depreciationMethod()))
        {
            TmpDateSum_ds.object(fieldNum(TmpDateSum, BeginningBalOfAccumulatedDep_JP)).visible(true);
            TmpDateSum_ds.object(fieldNum(TmpDateSum, YTDAccumulatedDep_JP)).visible(true);
        }
        else
        {
            TmpDateSum_ds.object(fieldNum(TmpDateSum, BeginningBalOfAccumulatedDep_JP)).visible(false);
            TmpDateSum_ds.object(fieldNum(TmpDateSum, YTDAccumulatedDep_JP)).visible(false);
        }
    }

    if (isIsoJP)
    {
        assetDepreciationProfile = assetBook.depreciationProfile();

        if (AssetDepreciationProfile::isDepMethod_JP(assetDepreciationProfile.Method))
        {
            depreciation.showZero(true);
        }

        isEquallyDiviedMethodUsed_JP = (
            (assetDepreciationProfile.Method == AssetDepreciationMethod::OldStraightLine_JP
                || assetDepreciationProfile.Method == AssetDepreciationMethod::OldDecliningBalance_JP)
            && assetBook.depreciationProfile().NumOfYearsEquallyDivided_JP > 0);
        TabEquallyDividedDepProfile_JP.visible(isEquallyDiviedMethodUsed_JP);

        isReductionEntry_JP = (AssetReductionEntryManager_JP::getAssetReductionEntryByAssetBook(
                                assetBook.AssetId
                                ,AssetBook.BookId
                                , AssetDocumentType_JP::AssetReductionEntry_Reserve) != null);
        TabReductionEntryReserve_JP.visible(isReductionEntry_JP);
        if (isReductionEntry_JP)
        {
            TmpDateSumReserve_JP_Allocation.showZero(true);
        }
        if (AssetBook.DepreciationExtProfile)
        {
            isSpecialDep_JP = (assetDepreciationProfile::isSpecialDepMethod_JP(assetBook.DepreciationExtProfile().Method) && assetBook.DepreciationExtProfile().SpecialDepAccountingMethod_JP == AssetSpecialDepAccountingMethod_JP::DirectOff);
            isSpecialDepReserve_JP = (assetDepreciationProfile::isSpecialDepMethod_JP(assetBook.DepreciationExtProfile().Method) && assetBook.DepreciationExtProfile().SpecialDepAccountingMethod_JP == AssetSpecialDepAccountingMethod_JP::Reserve);
            TabSpecialDepComparison_JP.visible(isSpecialDepReserve_JP && isCatchUpMethodUsed_JP);
            TmpDateSum_AdditionalDepAmount_JP.visible(isSpecialDep_JP);
            TabSpecialDepReserve_JP.visible(isSpecialDepReserve_JP && isCatchUpMethodUsed_JP);
        }
        if (AssetBook.depreciationAcceleratedProfile_JP().Profile)
        {
            isAcceleratedDep_JP = true;
            TmpDateSum_ds.object(fieldNum(TmpDateSum, AcceleratedDepAmount_JP)).visible(isAcceleratedDep_JP);
        }
    }
    // </GJP>

    element.initTmpDateSum();
}

public int selectionChange()
{
    // <GIN>
    int ret;

    ret = super();
    selection = typeOfShift.selection();
    element.initTmpDateSum();
    return ret;
    // </GIN>
}
