}

/// <summary>
///    Changes the 'AllowEdit'/'AllowDelete'/'AllowCreate' property of various fields and tables.
/// </summary>

void toggleAssetRuleThreshold()
{
    if (assetRule)
    {
        assetRuleEcoResCategory_ds.allowCreate(true);
        assetRuleThreshold_ds.allowCreate(true);
        assetRuleLocal_ds.allowCreate(true);
        assetRuleLocal_ds.allowEdit(true);
    }
    else
    {
        assetRuleEcoResCategory_ds.allowCreate(false);
        assetRuleThreshold_ds.allowCreate(false);
        assetRuleLocal_ds.allowCreate(false);
        assetRuleLocal_ds.allowEdit(false);
    }

    assetRuleQualifier_ds.object(fieldNum(AssetRuleQualifier, DefaultText)).allowEdit(assetRule.HasQualifiers);
    qualifierAssetRuleTxt.enabled(assetRule.HasQualifiers);
    optionAssetRuleTxt.enabled((assetRule.HasQualifiers && assetRuleQualifierOption));

    assetRuleQualifierOption_ds.allowCreate(assetRule.HasQualifiers);
    assetRuleQualifierOption_ds.allowEdit(assetRule.HasQualifiers);

    newAssetRuleQualifierOption.enabled(assetRule.HasQualifiers);
    deleteAssetRuleQualifierOption.enabled(assetRule.HasQualifiers && assetRuleQualifierOption.RecId);

    deleteEcoResCategory.enabled(assetRuleEcoResCategory.RecId);
    deleteGlobalRuleThreshold.enabled(AssetRuleThreshold::findByAssetRule(assetRule.RecId).RecId);

    element.toggleAssetRuleThresholdLocal();
}

/// <summary>
///    Changes the 'AllowEdit'/'AllowDelete'/'AllowCreate' property of various fields and tables.
/// </summary>
void toggleAssetRuleThresholdLocal()
{
    assetRuleQualifierLocal_ds.object(fieldNum(AssetRuleQualifierLocal, DefaultText)).allowEdit(assetRuleLocal.HasQualifiers);
    qualifierLocalAssetRuleTxt.enabled(assetRuleLocal.HasQualifiers);
    optionLocalAssetRuleTxt.enabled((assetRuleLocal.HasQualifiers && assetRuleQualifierOptionLocal));

    assetRuleQualifierOptionLocal_ds.allowCreate(assetRuleLocal.HasQualifiers);
    assetRuleQualifierOptionLocal_ds.allowEdit(assetRuleLocal.HasQualifiers);

    deleteLocalRuleThreshold.enabled(AssetRuleThresholdLocal::findByAssetRule(assetRule.RecId, localRuleDataAreaContext.value()).RecId);

    newAssetRuleQualifierOptionLocal.enabled(assetRuleLocal.HasQualifiers);
    deleteAssetRuleQualifierOptionLocal.enabled(assetRuleLocal.HasQualifiers && assetRuleQualifierOptionLocal.RecId);
}

