/// <summary>
///     Returns a warning icon or tick mark icon according to the configuration of asset rule.
/// </summary>
/// <param name="_assetRule">
///     A record in the table <c>AssetRule</c> table.
/// </param>
/// <returns>
///     Container with the Warning icon if the asset rule has missing or invalid configuration; otherwise, Accept icon.
/// </returns>
display container validateAssetRule(AssetRule _assetRule)
{
    ImageReference  imageReference;
    container       imageContainer;

    AssetRuleValidationStatus   assetRuleValidationStatus = _assetRule.checkAssetRuleInLegalEntity(LocalRuleDataAreaContext.value());

    switch (assetRuleValidationStatus)
    {
        case AssetRuleValidationStatus::Valid:
            // Don't show any icon if valid
            break;

        default:
            imageReference = ImageReference::constructForSymbol(ImageReferenceSymbol::Warning);
            break;
    }

    if (imageReference)
    {
        imageContainer = imageReference.pack();
    }

    return imageContainer;
}

