public void initValue()
{
    AssetRuleThreshold  assetRuleThresholdOrganizationWide;
    date                minValue = dateNull();
    date                maxValue = dateMax();

    super();

    select firstonly validTimeState(minValue, maxValue) Currency from assetRuleThresholdOrganizationWide
        order by CreatedDateTime desc
            where assetRuleThresholdOrganizationWide.AssetRule == assetRule.RecId ;

    assetRuleThreshold.Currency = assetRuleThresholdOrganizationWide.Currency;// defaulting currency of the last threshold configured for the rule
}

