/// <summary>
///     Displays legal-entity specific threshold amount in organization-wide currency.
/// </summary>
/// <param name = "_buyingLegalEntity">
///     Buyng legal entity for which records are to be retrieved from.
/// </param>
public void showAmountInSharedCur(RefRecId _buyingLegalEntity)
{
    Price                       price;
    CurrencyCode                legalEntityThresholdCurrencyCode;
    CurrencyCode                organizationWideThresholdCurrencyCode;

    if (assetRuleThresholdLocal)
    {
        organizationWideThresholdCurrencyCode   = AssetRuleThreshold::findByAssetRule(assetRule.RecId, assetRuleThresholdLocal.ValidFrom, assetRuleThresholdLocal.ValidTo).Currency;
        legalEntityThresholdCurrencyCode        = AssetRuleThresholdLocal::findByAssetRule(assetRule.RecId, localRuleDataAreaContext.value(), assetRuleThresholdLocal.ValidFrom, assetRuleThresholdLocal.ValidTo).Currency;

        element.getExchangeRateInfo(organizationWideThresholdCurrencyCode, legalEntityThresholdCurrencyCode, assetRuleThresholdLocal.ValidFrom);

        if (organizationWideThresholdCurrencyCode)
        {
            if (currencyExchangeRate || (organizationWideThresholdCurrencyCode == legalEntityThresholdCurrencyCode))
            {
                price = AssetRuleThresholdLocal::getLocalAmountInSharedCur(assetRule.RecId, localRuleDataAreaContext.value(), assetRuleThresholdLocal.ValidFrom, assetRuleThresholdLocal.ValidTo);
                convertedAmount.text(strFmt("@SYS314878", price, organizationWideThresholdCurrencyCode));
            }
            else
            {
                convertedAmount.text(strFmt("@SYS343202", legalEntityThresholdCurrencyCode, organizationWideThresholdCurrencyCode, assetRuleThresholdLocal.ValidFrom));
            }
        }
    }

    else
    {
        convertedAmount.text("");
    }
}

/// <summary>
/// Shows warning message related to icon.
/// </summary>
public void clicked()
{
    super();

    AssetRuleValidationStatus   assetRuleValidationStatus = assetRule.checkAssetRuleInLegalEntity(LocalRuleDataAreaContext.value());

    switch (assetRuleValidationStatus)
    {
        case AssetRuleValidationStatus::NoCategory:
            warning("@SYS137160");
            break;

        case AssetRuleValidationStatus::NoThreshold:
            warning("@SYS137158");
            break;

        case AssetRuleValidationStatus::ZeroThreshold:
            warning(strfmt("@SYS137159", AssetRuleThreshold::findByAssetRule(assetRule.RecId).Currency));
            break;

        case AssetRuleValidationStatus::LEThresholdHigher:
            warning("@SYS342564");
            break;

        case AssetRuleValidationStatus::NoExchangeRate:
            warning(strfmt("@SYS330455", assetRuleThresholdLocal.Currency, assetRuleThreshold.Currency));
            break;

        case AssetRuleValidationStatus::LEThresholdHigherBasedOnExchangeRate:
            warning(strfmt("@SYS330454", currencyExchangeRate, assetRuleThresholdLocal.Currency, exchangeRateDisplayFactor, assetRuleThreshold.Currency));
            break;
    }
}

