[Form]
public class AssetSum extends FormRun
{
    #ISOCountryRegionCodes

    AssetSumCalc    assetSumTrans;
    AssetSumCalc    assetSumTransMST_RU;
    AssetSumCalc    assetSumBudget;
    AssetSumCalc    assetSumTransReportingCurrency;
    // <GJP>
    AssetImpairmentSummary_JP  impairmentSum;
    AssetDocumentReductionEntry_JP  reductionEntryDocument;
    AssetReductionEntryProfile_JP   reductionEntryProfile;
    AssetAcceleratedDepSummary_JP   acceleratedDepSum;
    // </GJP>

    AssetYear           assetYear;
    AssetBudgetModelId  budgetModel;

    AssetId         assetId;
    AssetBookId     bookId;

    boolean isIsoRU;
    boolean isIsoIN;

    #define.CurrentVersion(1)
    #localmacro.CurrentList
        assetYear,
        budgetModel
    #endmacro

}

public void activate(boolean _active)
{
    super(_active);

    if (isIsoIN
        && !AssetParameters::find().DepreciationIncomeTaxAct_IN)
    {
        BonusValueRow.visible(false);
    }
    else
    {
        BonusValueRow.visible(true);
    }
}

edit AssetYear assetYear(boolean    set,
                         AssetYear  _assetYear)
{
    if (set)
    {
        assetYear = _assetYear;

        this.updateTotals();
    }
    return assetYear;
}

edit AssetBudgetModelId budgetModel(boolean set, AssetBudgetModelId _budgetModel)
{
    if (set)
    {
        budgetModel = _budgetModel;

        this.updateTotals();
    }
    return budgetModel;
}

