public display AssetTransDate lastTransDate_RU()
{
    return dateNull();
}

public void init()
{
    if (element.args() && element.args().dataset() == tableNum(AssetBook))
    {
        AssetBook localAssetBook;
        localAssetBook.data(element.args().record());
        assetId = localAssetBook.AssetId;
        bookId = localAssetBook.BookId;
    }

    if (!element.args() || (!assetId && !bookId))
    {
        throw error(strFmt("@SYS25516",element.name()));
    }

    isIsoIN = SysCountryRegionCode::isLegalEntityInCountryRegion([#isoIN]);
    isIsoRU = SysCountryRegionCode::isLegalEntityInCountryRegion([#isoRU]);

    super();

    xSysLastValue::getLast(this);
    assetYearField.selectedMenuOption(assetYear);
    budgetModelField.text(budgetModel);

    // <GEEU>
    actualPreAcquisitionValue_CZ.visible(AssetParameters::find().AllowPreAcquisition_CZ != AssetAllowPreAcquisition_CZ::No);
    budgetPreAcquisitionValue_CZ.visible(AssetParameters::find().AllowPreAcquisition_CZ != AssetAllowPreAcquisition_CZ::No);
    // </GEEU>

    this.updateTotals();

    // <GJP>
    if (AssetDocumentUtility_JP::isAssetDocumentEnabled())
    {
        switch (AssetParameters::accountingStandard_JP())
        {
            case AssetAccountingStandard_JP::JPGAAP:
                UndiscountedCashFlowEdit.visible(true);
                UndiscCashFlowLastUpdateEdit.visible(true);
                break;
            case AssetAccountingStandard_JP::IFRS:
                UndiscountedCashFlowEdit.visible(false);
                UndiscCashFlowLastUpdateEdit.visible(false);
                break;
        }
        this.initReductionEntry_JP();
    }
    // </GJP>

    this.makeVisibleReportingCurrencyAmountFields();
}

/// <summary>
/// Initializes reduction entry related paramter
/// </summary>
public void initReductionEntry_JP()
{
    reductionEntryDocument = AssetReductionEntryManager_JP::getAssetReductionEntryDocByAssetBook(assetId, bookId);
    if (reductionEntryDocument)
    {
        reductionEntryProfile = AssetReductionEntryProfile_JP::find(reductionEntryDocument.parmAssetDocumentTable().DocumentId);
        ReductionEntry.visible(true);
    }
}

public boolean unpack(container packedClass)
{
    Integer version = conPeek(packedClass,1);

    switch (version)
    {
        case #CurrentVersion:
            [version, #CurrentList] = packedClass;
            break;
        default:
            return false;
    }

    return true;
}

void updateTotals()
{
    // <GJP>
    AssetDocumentSumCalcUtil_JP sumCalcUtil;
    AssetSumCalc_Trans          sumCalcTrans;
    // </GJP>

    assetSumTrans   = AssetSumCalc_Trans::newAssetYear (assetId, bookId, DateTimeUtil::getSystemDate(DateTimeUtil::getUserPreferredTimeZone()), assetYear);

    if (Ledger::reportingCurrency() != '')
    {
        assetSumTransReportingCurrency = AssetSumCalc_Trans::newAssetYearByCurrency(assetId,
                                                                        bookId,
                                                                        DateTimeUtil::getSystemDate(DateTimeUtil::getUserPreferredTimeZone()),
                                                                        assetYear,
                                                                        AssetSumCalcType::Reporting);
    }

    // <GJP>
    if (AssetDocumentUtility_JP::isAssetDocumentEnabled())
    {
        sumCalcTrans = assetSumTrans as AssetSumCalc_Trans;

        if (sumCalcTrans != null)
        {
            sumCalcUtil = AssetDocumentSumCalcUtil_JP::construct();
            sumCalcTrans.parmDocSumCalcUtil_JP(sumCalcUtil);
            impairmentSum = AssetImpairmentSummary_JP::construct(assetId, bookId, AssetBookType::ValueModel, sumCalcTrans, sumCalcUtil, DateTimeUtil::getSystemDate(DateTimeUtil::getUserPreferredTimeZone()), assetYear);
            acceleratedDepSum = AssetAcceleratedDepSummary_JP::construct(assetId, bookId, AssetBookType::ValueModel, sumCalcTrans, sumCalcUtil, DateTimeUtil::getSystemDate(DateTimeUtil::getUserPreferredTimeZone()), assetYear);
            sumCalcTrans.calc();
            impairmentSum.init();
            acceleratedDepSum.init();
        }
    }
    // </GJP>

    assetSumBudget = AssetSumCalc_Budget::newAssetYear(assetId, bookId, budgetModel, DateTimeUtil::getSystemDate(DateTimeUtil::getUserPreferredTimeZone()), assetYear);
}

