/// <summary>
/// Provides the name of the tree item for the specified asset.
/// </summary>
/// <param name = "_assetId">
/// A fixed asset number.
/// </param>
/// <returns>
/// The name of the tree item for the specified asset.
/// </returns>
protected str treeItemNameForAsset(AssetId _assetId)
{
    return _assetId;
}

/// <summary>
///     Adding the additional asset numbers to the tree.
/// </summary>
/// <param name = "_assetId">
///     The main asset ID.
/// </param>
/// <param name = "_treeHeader">
///     A parent position in the tree structure.
/// </param>
/// <param name = "_list">
///     A boolean value to list, optional.
/// </param>
void buildComposedOf(AssetId _assetId,
    int _treeHeader,
    NoYes _list = NoYes::No)
{
    if (!_assetId)
    {
        return;
    }

    container childList = AssetTable::childList(_assetId);
    Counter childListLength = conLen(childList);

    for (Counter childIdx = 1; childIdx <= childListLength; childIdx++)
    {
        AssetId childAssetId = conPeek(childList, childIdx);

        FormTreeItem formTreeItem = new FormTreeItem(this.treeItemNameForAsset(childAssetId), imageListAppl.image(AssetTable::treeImage(childAssetId)), -1, childAssetId);

        int node = structure.addItem(_treeHeader, FormTreeAdd::Sort, formTreeItem);

        this.buildComposedOf(childAssetId, node, _list || (_assetId == childAssetId));

        if (childAssetId == assetTable.AssetId)  // current item
        {
            structure.select(node);
        }
    }
    structure.expand(_treeHeader, FormTreeExpand::Expand);
}

/// <summary>
/// This method determines if all value models have status as not acquired.
/// </summary>
/// <returns>
/// true if all value models have status not acquired; otherwise, false.
/// </returns>
private boolean checkIfNotAcquired()
{
    AssetBook assetBook;
    AssetId assetId;

    assetId = assetTable.AssetId;

    if (assetId)
    {
        select firstOnly RecId from assetBook
            where assetBook.assetId == assetId
                && assetBook.Status != AssetStatus::NoAcquisition;

        if (assetBook.RecId)
        {
            return false;
        }
    }

    return true;
}

