public void close()
{
    if (! assetTable.RecId && assetIdNumberSeq)
    {
        assetIdNumberSeq.abort();
    }

    if (! assetTable.RecId && barcodeNumberSeq)
    {
        barcodeNumberSeq.abort();
    }

    super();
}

/// <summary>
/// Creates the form tree node structure of fixed asset numbers.
/// </summary>
void createStruct()
{
    AssetId assetIdTop = assetTable.topMainAsset();

    // This is not a threat because it is only adding additional asset numbers to the tree.
    FormTreeItem formTreeStructure = new FormTreeItem(this.treeItemNameForAsset(assetIdTop), imageListAppl.image(AssetTable::treeImage(assetIdTop)), -1, assetIdTop);
    structure.delete(structure.getRoot());
    this.buildComposedOf(assetIdTop, structure.addItem(0, FormTreeAdd::Sort, formTreeStructure));

    buildStructure = false;
}

public void customizeMenuOptions(OfficeMenuOptions _menuOptions)
{
    this.removeAutoAddedEntitiesFromMenu(_menuOptions);
}

private void removeAutoAddedEntitiesFromMenu(OfficeMenuOptions _menuOptions)
{
    ListIterator dataEntityOptionsIterator = new ListIterator(_menuOptions.dataEntityOptions());

    while (dataEntityOptionsIterator.more())
    {
        OfficeMenuDataEntityOptions dataEntityOptions = dataEntityOptionsIterator.value();

        if (dataEntityOptions.dataEntityName() != tableStr(AssetFixedAssetV2Entity))
        {
            dataEntityOptionsIterator.delete();
        }
        else
        {
            dataEntityOptionsIterator.next();
        }
    }
}

public ExportToExcelDataEntityContext getDataEntityContext(OfficeGeneratedExportMenuItem _menuItem)
{
    ExportToExcelDataEntityContext context = null;

    switch (_menuItem.dataEntityName())
    {
        case tablestr(AssetFixedAssetV2Entity):
            context = ExportToExcelDataEntityContext::constructDefault(
                tablestr(AssetFixedAssetV2Entity));
            break;
    }

    return context;
}

