/// <summary>
/// Updates the design based on asset type.
/// </summary>
public void updateAssetTypeDesign_JP()
{
    if (AssetTable.AssetType == AssetType::Deferred_JP)
    {
        AssetTable_ds.object(fieldNum(AssetTable, AssetClassification_JP)).visible(false);
        AssetTable_ds.object(fieldNum(AssetTable, AssetDeferredType_JP)).visible(true);
        AssetTable_ds.object(fieldNum(AssetTable, AssetDeferredType_JP)).mandatory(true);
    }
    else
    {
        AssetTable_ds.object(fieldNum(AssetTable, AssetClassification_JP)).visible(true);
        AssetTable_ds.object(fieldNum(AssetTable, AssetDeferredType_JP)).visible(false);
        AssetTable_ds.object(fieldNum(AssetTable, AssetDeferredType_JP)).mandatory(false);
    }
}

    void updateDesign()
    {
        NumberSequenceTable numberSequence;
        AssetGroup assetGroup;

        // Enable/disable fields

        assetNum.allowEdit(assetTable.allowEditOfAssetNum());
        assetGroup = assetTable.assetGroup();

        numberSequence = this.findAssetIdNumberSequence();

        if (assetGroup
            && numberSequence
            && (numberSequence.Manual == NoYes::No 
				|| numberSequence.Continuous)
            && !numberSequence.AllowChangeDown
            && !numberSequence.AllowChangeUp)
        {
            identification_AssetId.allowEdit(false);
        }
        else
        {
            identification_AssetId.allowEdit(true);
        }

        // <GEELT>
        if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoLT]))
        {
            boolean editLocation = this.checkIfNotAcquired();

            assetTable_DS.object(fieldNum(AssetTable, Location)).allowEdit(editLocation);
            assetTable_DS.object(fieldNum(AssetTable, WorkerResponsible)).allowEdit(editLocation);
            assetTable_DS.object(fieldNum(AssetTable, Department)).allowEdit(editLocation);
        }
        // </GEELT>

        // <GCN><GJP>
        if (isIsoJP || isIsoCN)
        {
            assetTable_DS.object(fieldNum(AssetTable, Location)).allowEdit(!assetTable.Location);
            if (isIsoCN || AssetParameters::isLocationTransferEnable_JP())
            {
                assetLocationTransfer_CN.enabled(assetTable.Location ? true : false);
            }
            else
            {
                assetLocationTransfer_CN.enabled(false);
            }
        }
        // </GJP></GCN>

        // <GJP>
        if (isIsoJP)
        {
            assetTable_DS.object(fieldNum(AssetTable, Location)).mandatory(true);
            this.updateAssetTypeDesign_JP();
        }
        // </GJP>

        physical_Barcode.allowEdit(!AssetParameters::find().BarcodeEqualsAssetNumber);

        this.updateDesign_BR();
    }

private NumberSequenceTable findAssetIdNumberSequence()
{
    NumberSequenceTable numberSequence;
    AssetGroup assetGroup;

    assetGroup = assetTable.assetGroup();
    if (assetGroup.AutoNumber)
    {
        numberSequence = NumberSequenceTable::find(assetGroup.AutoNumberSequenceTable);
    }

    if (!numberSequence && AssetParameters::find().AutoNumber)
    {
        numberSequence = Assetparameters::numRefAssetId().numberSequenceTable();
    }

    return numberSequence;
}

void updateDesign_BR()
{
    FBPISCOFINSFixedAssetAppropMethod_BR appropriationMethod;
    boolean                              allowPISCOFINSCredit = AssetTable.PISCOFINSReceivableLongTerm_BR;

    if (BrazilParameters::isEnabled())
    {
        assetTable_DS.object(fieldNum(AssetTable, PISCOFINSCreditGroup_BR)).allowEdit(allowPISCOFINSCredit);
        assetTable_DS.object(fieldNum(AssetTable, PISCOFINSCreditPurpose_BR)).allowEdit(allowPISCOFINSCredit);
        assetTable_DS.object(fieldNum(AssetTable, PISCOFINSAppropriationMethod_BR)).allowEdit(allowPISCOFINSCredit);
        assetTable_DS.object(fieldNum(AssetTable, PISTaxationCode_BR)).allowEdit(allowPISCOFINSCredit);
        assetTable_DS.object(fieldNum(AssetTable, COFINSTaxationCode_BR)).allowEdit(allowPISCOFINSCredit);

        appropriationMethod = assetTable_DS.object(fieldNum(AssetTable, PISCOFINSAppropriationMethod_BR)).getValue();
        assetTable_DS.object(fieldNum(AssetTable, PISCOFINSCreditInstallments_BR)).allowEdit(appropriationMethod == FBPISCOFINSFixedAssetAppropMethod_BR::Acquisition);
    }
}

int active()
{
    int ret;
    // <GJP>
    boolean            allowEditLoc = false;
    AssetDocument_JP   document;
    // </GJP>

    ret = super();

    buildStructure = true;

    if (structureTab.isExpanded())
    {
        element.createStruct();
    }

    element.updateDesign();

    // <GIN>
    if (AssetParameters::checkAssetParameters_IN())
    {
        element.enableDisableChangeAsset_IN();
    }
    // </GIN>

    // <GJP>
    if (AssetDocumentUtility_JP::isAssetDocumentEnabled())
    {
        document = AssetReductionEntryManager_JP::getAssetReductionEntryDocByAssetBook(
            assetTable.AssetId,
            '');
        if (document != null)
        {
            allowEditLoc = true;
        }
        ReductionEntryDocumentDate_JP.allowEdit(allowEditLoc);
    }
    // </GJP>

    return ret;
}

public void write()
{
    boolean isNewRecord = !assetTable.RecId;
    ttsbegin;

    if (assetIdNumberSeq)
    {
        assetIdNumberSeq.onNumberUsed(assetTable.AssetId, assetIdAllocated);
    }

    if (!assetTable.Barcode && isNewRecord)
    {
        if (AssetParameters::find().BarcodeEqualsAssetNumber == NoYes::Yes)
        {
            assetTable.Barcode  = assetTable.AssetId;
        }
        else
        {
            barcodeNumberSeq = assetTable.initBarcodeNumberSeq();
            if (barcodeNumberSeq)
            {
                assetTable.Barcode = barcodeNumberSeq.num();
                barcodeNumberSeq.used();
            }
        }
    }

    if  (BrazilParameters::isEnabled())
    {
        if (!assetTable.PISCOFINSReceivableLongTerm_BR)
        {
            AssetTable.PISCOFINSCreditGroup_BR          = FBPISCOFINSFixedAssetCreditGroup_BR::None;
            AssetTable.PISCOFINSCreditInstallments_BR   = 0;
            AssetTable.PISCOFINSCreditPurpose_BR        = FBPISCOFINSFixedAssetCreditPurpose_BR::None;
            AssetTable.PISCOFINSAppropriationMethod_BR  = FBPISCOFINSFixedAssetAppropMethod_BR::None;
            AssetTable.PISTaxationCode_BR               = '';
            AssetTable.COFINSTaxationCode_BR            = '';
        }
    }

    super();

    ttscommit;
}

void modified()
{
    super();

    if (assetIdNumberSeq)
    {
        assetIdNumberSeq.abort();
    }

    assetIdNumberSeq = assetTable.initAssetNumberSeq();
    if (assetIdNumberSeq)
    {
        assetTable.AssetId = assetIdNumberSeq.num();
        assetIdAllocated   = assetTable.AssetId;
    }

    FormDataUtil::getFormDataSource(assetTable).refresh();

    element.updateDesign();
}

