            public void lookup(FormControl _formControl, str _filterStr)
			{
                if (isIsoJP)
                {
			        SysTableLookup          sysTableLookup;
			        QueryBuildDataSource    queryBuildDS;
			        Query                   query;

			        query = new Query();

			        queryBuildDS = query.addDataSource(tableNum(WrkCtrTable));
			        queryBuildDS.addRange(fieldNum(WrkCtrTable, IsIndividualResource)).value(
			            queryValue(NoYes::Yes));
			        sysTableLookup = SysTableLookup::newParameters(tableNum(WrkCtrTable), _formControl);
			        sysTableLookup.addLookupfield(fieldNum(WrkCtrTable, WrkCtrId));
			        sysTableLookup.parmQuery(query);
			        sysTableLookup.performFormLookup();
                }
                else
                {
                    super(_formControl, _filterStr);
                }
			}

