                if (ret && isIsoJP && AssetTable.WrkCtrId_JP && !WrkCtrTable::find(AssetTable.WrkCtrId_JP).IsIndividualResource)
			    {
			        ret = checkFailed(strFmt("@FixedAssetsIncentiveDepreciation:SYP4880082"));
			    }

			    return ret;
			}

public void init()
{
    if (element.args().menuItemName() == menuitemDisplayStr(AssetTableAssetNameTooLongListPage))
    {
        this.linkType(FormLinkType::ExistJoin);
    }

    super();

    if (element.args().menuItemName() == menuitemDisplayStr(AssetTableAssetNameTooLongListPage))
    {
        AssetNameTooLongTmp tmp = element.args().record();
        element.startupDataAreaId = tmp.DataArea;

        if (element.startupDataAreaId != curExt())
        {
            assetTable_ds.query().allowCrossCompany(true);
            assetTable_ds.query().clearCompanyRange();
            assetTable_ds.query().addCompanyRange(element.startupDataAreaId);
        }

        AssetNameTooLongTmp assetNameTmp;

        SysDictTable sysDictAssetTable = new SysDictTable(tableNum(AssetTable));
        str assetTableName = sysDictAssetTable.name(DbBackend::Sql);
        str assetName = sysDictAssetTable.fieldObject(fieldNum(AssetTable, Name)).name(DbBackend::Sql);
        str assetId = sysDictAssetTable.fieldObject(fieldNum(AssetTable, AssetId)).name(DbBackend::Sql);
        str assetDataAreaId = sysDictAssetTable.fieldObject(fieldNum(AssetTable, DataAreaId)).name(DbBackend::Sql);
        str assetPartition = sysDictAssetTable.fieldObject(fieldNum(AssetTable, Partition)).name(DbBackend::Sql);

        str sqlStatementText = strFmt(@"
            INSERT %6 (AssetId, DataArea)
            SELECT T.%1, T.%2
            FROM %3 AS T
            WHERE LEN(T.[%4]) > @ASSETNAMELIMIT
            AND T.%2 = @DATAAREAID
            AND T.%5 = @PARTITION",
            assetId,
            assetDataAreaId,
            assetTableName,
            assetName,
            assetPartition,
            assetNameTmp.getPhysicalTableName());

        Map paramMap = SqlParams::create();
        paramMap.add('ASSETNAMELIMIT', AssetConstants::AssetNameMaxLength);
        paramMap.add('DATAAREAID', element.startupDataAreaId);
        paramMap.add('PARTITION', getCurrentPartitionRecId());

        UserConnection connection = new UserConnection();

        try
        {
            Statement statement = connection.createStatement();
            new SqlStatementExecutePermission(sqlStatementText).assert();

            statement.executeUpdateWithParameters(sqlStatementText, paramMap);

            statement.close();
            CodeAccessPermission::revertAssert();
            AssetNameTooLongTmp.linkPhysicalTableInstance(assetNameTmp);
        }
        finally
        {
            connection.finalize();
        }

        QueryBuildDataSource assetNameTmpQbds = this.query().dataSourceTable(tableNum(AssetNameTooLongTmp));
        assetNameTmpQbds.relations(true);
        assetNameTmpQbds.joinMode(JoinMode::InnerJoin);
        assetNameTmpQbds.addLink(fieldNum(AssetTable, AssetId), fieldNum(AssetNameTooLongTmp, AssetId));
    }
}

