public str resolveAmbiguousReference()
{
    str ret;

    if (this.shouldResolveAmbiguousReference())
    {
        ret = super();
    }
    else
    {
        ret = this.text();
    }

    return ret;
}

private boolean shouldResolveAmbiguousReference()
{
    boolean ret = true;

    if (AssetTableFormResolveAmbiguousReferenceFlight::instance().isEnabled()
        && identification_AssetId.isEditable()
        && element.findAssetIdNumberSequence().Manual == NoYes::Yes)
    {
        ret = false;
    }

    return ret;
}

