/// <summary>
/// Validates invalid asset activity code from <c>AssetActivityCode</c> table.
/// </summary>
/// <returns>
/// true if the asset activity code exist in <c>AssetActivityCode</c> table, otherwise, false.
/// </returns>
public boolean validate()
{
    boolean ret;

    ret = super();

    if (this.text() && !AssetActivityCode::findByAssetActivityCode(this.text()))
    {
        ret = checkFailed(strFmt("@SYS31784", this.text(),
        fieldPName(AssetActivityCode, AssetActivityCode), tablePName(AssetActivityCode)));
    }

    return ret;
}

