[Form]
public class AssetTrans extends FormRun
{
    #ISOCountryRegionCodes

    boolean isAssetDocumentAROEnabled;
    boolean isIsoDEAT = SysCountryRegionCode::isLegalEntityInCountryRegion([#isoDE, #isoAT]);

}

public void init()
{
    QueryBuildDataSource dataSource;

    // <GJP>
    if (AssetDocumentUtility_JP::isAssetDocumentEnabled()
        && element.args().menuItemName() == menuitemDisplayStr(AssetRetirementObligationTrans_JP))
    {
        isAssetDocumentAROEnabled = true;
    }
    // </GJP>

    super();

    if (this.args().dataset() != tablenum(AssetBook))
        assetTrans_BookId.visible(true);
    else
        assetTrans_BookId.visible(false);

    // Manually change the sort order for AssetTrans to be AssetId, TransDate to maintain sort order.
    dataSource = assetTrans_ds.query().dataSourceTable(tablenum(AssetTrans));

    dataSource.addOrderByField(fieldnum(AssetTrans, AssetId));
    dataSource.addOrderByField(fieldnum(AssetTrans, TransDate));

    // <GIN>
    if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoIN])
        && !AssetParameters::isDepreciationIncomeTaxActEnable_IN())
    {
        assetTrans_ds.object(fieldnum(AssetTrans, AssetGroup)).visible(false);
    }
    // </GIN>

    // <GJP>
    if (isAssetDocumentAROEnabled)
    {
        this.initialDocumentDesign_JP();
    }
    // </GJP>

    if (Ledger::reportingCurrency() && Ledger::reportingCurrency() != Ledger::accountingCurrency())
    {
        AssetTrans_ReportingCurrencyAmount.visible(true);
    }
}

private void openVoucher(Voucher _voucher, TransDate _transDate)
{
    Args args = new Args();
    MenuFunction menuFunction;

    menuFunction = new MenuFunction(menuitemDisplayStr(LedgerTransVoucher1), MenuItemType::Display);

    args.caller(this);

    args.lookupRecord(AssetTrans::findByVoucher(_voucher, _transDate));
    menuFunction.run(args);
}

/// <summary>
/// Finds if the transaction was part of a journal which can post to ledger.
/// </summary>
/// <returns>true if the journal can post to ledger, else; false.</returns>
private boolean isJournalPostingToLedger()
{
    boolean ret = true;
    LedgerJournalTrans ledgerJournalTrans;
    LedgerJournalTable ledgerJournalTable;

    select firstonly JournalNum from ledgerJournalTrans
        where ledgerJournalTrans.Voucher == assetTrans.Voucher
            && ledgerJournalTrans.TransDate == assetTrans.TransDate;

    if (ledgerJournalTrans)
    {
        select firstonly CurrentOperationsTax from ledgerJournalTable
            where  ledgerJournalTable.JournalNum == ledgerJournalTrans.JournalNum;

        if (ledgerJournalTable.CurrentOperationsTax == CurrentOperationsTax::None)
        {
            ret = false;
        }
    }
    else
    {
        ret = AssetBookTable::find(assetTrans.BookId).postToGeneralLedger();
    }

    return ret;
}

/// <summary>
/// Sets the editable property of various datasource fields for the form.
/// </summary>
public void enableFieldsActive()
{
    if (isIsoDEAT)
    {
        if (assetTrans.isInterestEnabled())
        {
            assetTrans_DS.allowEdit(true);
            allowEditFieldsOnFormDS_W(assetTrans_DS, false);
            assetTrans_DS.object(fieldNum(AssetTrans, InterestAmount)).allowEdit(true);
        }
        else
        {
            assetTrans_DS.allowEdit(false);
        }
    }
}

public void selectionChanged()
{
    boolean isAcquisition;
    boolean isDisposal;

    #ISOCountryRegionCodes

    if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoCZ, #isoLT, #isoLV, #isoPL]))
    {
        isAcquisition = assetTrans.isAcquisitionTransaction();
        isDisposal    = assetTrans.isDisposalTransaction();

        AssetAcquisitionDocumentPL.enabled(isAcquisition);
        AssetAcquisitionStatement.enabled(isAcquisition);
        AssetExploitation.enabled(isAcquisition);

        AssetDisposalActCZLV.enabled(isDisposal);
        AssetDisposalDocumentPL.enabled(isDisposal);

        if (assetTrans && SysCountryRegionCode::isLegalEntityInCountryRegion([#isoLT]))
        {
            // For LT, the individual menu items are enabled for all transaction types so
            // enable the Print button as long as a transaction exists.
            PrintButton.enabled(true);
        }
        else
        {
            // Enable if any item under Print button is enabled
            PrintButton.enabled(isAcquisition || isDisposal);
        }
    }

    super();
}

public int active()
{
    int ret;

    ret = super();

    assetReverseTransBase.enabled(TransactionReversalTrans::findTransactionReversalTrans(tablenum(AssetTrans), assetTrans.RecId) ? true : false);

    element.enableFieldsActive();

    return ret;
}

public void write()
{
    if (isIsoDEAT)
    {
        return;
    }

    super();
}

public boolean validateWrite()
{
    boolean ret;

    if (isIsoDEAT)
    {
        return true;
    }

    ret = super();

    return ret;
}

/// <summary>
/// Open either the voucher form or asset transaction origin document form
/// </summary>
public void clicked()
{
    Args args;

    args = new Args();
    args.caller(element);
    args.record(assetTrans);

    boolean postToLedger;

    postToLedger = element.isJournalPostingToLedger();

    if (postToLedger)
    {
        new MenuFunction(menuitemdisplaystr(LedgerTransVoucher1), MenuItemType::Display).run(args);
    }
    else
    {
        new MenuFunction(menuitemdisplaystr(AssetBookOriginaldocuments), MenuItemType::Display).run(args);
    }
}

void clicked()
{
    if (AssetTrans::checkReversal(assetTrans, true))
    {
        super();
        assetTrans_ds.research();
    }
    else
    {
        throw error(strFmt("@FixedAssets:FA_AssetTrans_ReverseButton_TransactionNotReversed", AssetTrans.Voucher));
    }
}

void clicked()
{
    // read so the doc date and doc num are updated
    assetTrans_ds.reread();

    super();
}

public void jumpRef()
{
    super();

    element.openVoucher(assetTrans.Voucher, assetTrans.TransDate);
}

public void jumpRef()
{
    super();

    element.openVoucher(assetTrans.Voucher, assetTrans.TransDate);
}
