public void closeOk()
{
    AssetTransfer           assetTransfer;
    AssetTransferBalancing  assetTransferBalancing;
    NoYesId                 confirm;
    MenuFunction            menuFunction;
    Args                    args;
    FormRun                 formRun;
    MultiSelectionHelper    multiSelectionHelper;
    AssetBook               assetBookLocal;
    DimensionDefault        defaultDimension;
    boolean                 isPublicSector;
    assetBookList = new RecordSortedList(tableNum(AssetBook));
    assetBookList.sortOrder(fieldNum(AssetTable, RecId));

    multiSelectionHelper = MultiSelectionHelper::construct();
    multiSelectionHelper.parmDatasource(AssetBook_ds);

    assetBookLocal = multiSelectionHelper.getFirst() as AssetBook;

    if (assetBookLocal.RecId == 0)
    {
        assetBookList.ins(assetBook);
    }

    while (assetBookLocal.RecId)
    {
        assetBookList.ins(assetBookLocal);
        assetBookLocal = multiSelectionHelper.getNext() as AssetBook;
    }

    args = new Args();
    args.parmObject(assetBookList);

    defaultDimension = DimensionEntryControlHeader.saveAttributeValueSet();
    args.parm(int642str(defaultDimension));

    args.record(assetBook);
    args.caller(element);

    menuFunction = new MenuFunction(menuitemDisplayStr(AssetTransferConfirmation), MenuItemType::Display);

    formRun = menuFunction.create(args);
    formRun.init();
    formRun.run();
    formRun.wait(true);

    confirm = formRun.closedOk();
    isPublicSector = isConfigurationkeyEnabled(configurationKeyNum(PublicSector));

    if (confirm)
    {
        ttsbegin;
        if (isPublicSector)
        {
            assetTransferBalancing = AssetTransferBalancing::newByDueFromDueTo(
                                assetBookMainAccountTransfer.DueFrom_PSN,
                                assetBookMainAccountTransfer.DueTo_PSN);
        }
        else
        {
            assetTransfer = AssetTransfer::construct();
        }

        if (isPublicSector)
        {
            if (assetTransferBalancing.isBalancing(defaultDimension)
            && (!assetBookMainAccountTransfer.DueFrom_PSN || !assetBookMainAccountTransfer.DueTo_PSN))
            {
                throw error(strFmt("@SPS1598", "@SPS1209", "@SPS1210"));
            }
            else
            {
                if (!assetTransferBalancing.transferAsset(
                        assetBookList,
                        defaultDimension,
                        transferDate.dateValue(),
                        transferComment.text()))
                {
                    throw error("@FixedAssets:FA_MessageError_ErrorsOccurredDuringTransferProcess");
                }
            }
        }
        else
        {
            //
            // Method transferAsset() of the AssetTransfer class appends warning messages to infolog in case of missing mandatory
            // fields. However, as no exception or error is thrown, super() is to be called conditionally. Otherwise,
            // calling super() when transfer has not been done successfully will result in the Asset Transfer form being closed
            // and the warning or error messages to appear in the infolog. Desired functionality is that user should be able
            // view the infolog and then be able to correct field values and transfer again.
            //
            if (!assetTransfer.transferAsset(
                        assetBookList,
                        defaultDimension,
                        transferDate.dateValue(),
                        transferComment.text()))
            {
                throw error("@FixedAssets:FA_MessageError_ErrorsOccurredDuringTransferProcess");
            }
        }

        ttsCommit;

        super();
    }
}

public void init()
{
    this.initAssetsToTransferSet();

    super();

    this.checkAssetBooksStatus();

    DimensionEntryControlHeader.parmCompany(curExt());
    DimensionEntryControlHeader.parmDisplayValues(true);
    DimensionEntryControlHeader.reactivate();

    transferDate.dateValue(DateTimeUtil::getSystemDate(DateTimeUtil::getUserPreferredTimeZone()));
}

private void initAssetsToTransferSet()
{
    assetsToTransferSet = new Set(Types::String);

    MultiSelectionContext multiSelectionContext = element.args().multiSelectionContext();

    AssetTable localAssetTable = multiSelectionContext.getFirst() as AssetTable;

    if (!localAssetTable)
    {
        // If no record was marked, get the record which had focus.
        localAssetTable =  element.args().record();
    }

    int selectedAssetCounter;
    // Add all assets that were selected earlier into the assetBook datasource's range.
    while (localAssetTable)
    {
        assetsToTransferSet.add(localAssetTable.AssetId);

        localAssetTable = multiSelectionContext.getNext() as AssetTable;
        selectedAssetCounter++;
    }

    // Show infolog warning and abort transfer if number of selected assets exceed maximum allowed assets to be transferred.
    if (selectedAssetCounter > #MaxAllowedAssetsForTransfer)
    {
        str limitationMessage   = strFmt("@SYS4005282", #MaxAllowedAssetsForTransfer);
        str suggestionMessage   = strFmt("@SYS4005283", #MaxAllowedAssetsForTransfer);
        str completeMessage     = strFmt("@FixedAssets:AssetTransferErrorMessageFormat", limitationMessage, suggestionMessage, "@SYS4005284");

        Box::stop(completeMessage, "@SYS115063");
        this.closeCancel();
    }
}

private void checkAssetBooksStatus()
{
    // Copy the main datasource query
    Query query = new Query(assetBook_ds.query());
    QueryBuildDataSource qbdsAssetBook = query.dataSourceTable(tableNum(AssetBook));

    // Finding all value models NOT in Open status to provide infolog warnings.
    QueryBuildRange statusRange = xSysQuery::findOrCreateRange(qbdsAssetBook, fieldNum(AssetBook, Status));
    statusRange.value(strfmt("%1,%2", SysQuery::valueNot(AssetStatus::Open), SysQuery::valueNot(AssetStatus::Closed)));

    QueryRun queryRun  = new QueryRun(query);

    // Check if any of the selected valuemodels are Not in open status.
    // Show infolog warning for all such selected value models.
    if (SysQuery::countLoops(queryRun) > 0)
    {
        warning("@FixedAssets:FA_MessageNotification_BooksCannotBeTransferedBecauseStatusIsWrong");

        while (queryRun.next())
        {
            AssetBook localAssetBook = queryRun.get(tableNum(AssetBook));

            warning(strFmt("@FixedAssets:FA_MessageNotification_AssetAndBookInformation", localAssetBook.AssetId, localAssetBook.BookId));

        }
    }

    statusRange = xSysQuery::findOrCreateRange(qbdsAssetBook, fieldNum(AssetBook, Status));
    statusRange.value(strfmt("%1,%2", SysQuery::value(AssetStatus::Open), SysQuery::value(AssetStatus::Closed)));

    //Close the form if no value models are selected that are in the Open or Closed status.
    if (SysQuery::countLoops(new QueryRun(query)) <= 0)
    {
        this.closeCancel();
    }
}

/// <summary>
///     Updates records in the financial dimensions grid upon changing the active value model.
/// </summary>
/// <returns>
///     Value which is returned by the base implementation.
/// </returns>
public int active()
{
    AssetTmpAssetTransferHistory    assetTmpHistory;
    RecordSortedList                valueModelList = new RecordSortedList(tableNum(AssetBook));

    valueModelList.ins(AssetBook);

    // Populates Financial Dimensions based on the Value Model currently selected.
    assetTmpHistory  = AssetTransfer::getFromAndToDimensions(valueModelList, AssetBook.DefaultDimension);

    assetTmpAssetTransferHistory.setTmpData(assetTmpHistory);
    AssetTmpAssetTransferHistory_ds.refresh();

    return super();
}

/// <summary>
///    Initializes the data source query.
///    The override makes sure that only fixed assets that were marked,
///    in the <c>AssetTableListPage</c> should be selected out.
/// </summary>
public void init()
{
    super();

    Query queryObject   = this.query();
    queryObject.literals(true);

    QueryBuildDataSource qbdsAssetBook = this.queryBuildDataSource();

    qbdsAssetBook.clearDynalinks();
    qbdsAssetBook.addRange(fieldnum(AssetBook, AssetId)).status(RangeStatus::Hidden);

    // <GIN>
    if (AssetParameters::isDepreciationCompaniesActEnable_IN()
        || AssetParameters::isDepreciationIncomeTaxActEnable_IN())
    {
        qbdsAssetBook.addRange(fieldnum(AssetBook, AssetGroupDepreciation_IN)).value(queryValue(NoYes::No));
    }
    // </GIN>

    SetEnumerator assetsToTransferSetEnumerator = assetsToTransferSet.getEnumerator();

    while (assetsToTransferSetEnumerator.moveNext())
    {
        qbdsAssetBook.addRange(fieldNum(AssetBook, AssetId)).value(assetsToTransferSetEnumerator.current());
    }

    QueryBuildRange statusRange = xSysQuery::findOrCreateRange(qbdsAssetBook, fieldNum(AssetBook, Status));
    statusRange.value(strfmt("%1,%2", SysQuery::value(AssetStatus::Open), SysQuery::value(AssetStatus::Closed)));
}

public void init()
{
    super();

    QueryBuildDataSource queryBuildDataSource = this.queryBuildDataSource();
    QueryBuildRange queryBuildRange = queryBuildDataSource.addRange(fieldNum(AssetBookTable, CurrentOperationsTax));

    queryBuildRange.status(RangeStatus::Locked);
    queryBuildRange.value(SysQuery::valueNot(CurrentOperationsTax::None));
}

public void init()
{
    super();

    // System generated links causing the AssetBook datasource's record to be written to (possibly because
    //  of AssetBook table's relation to itself.)  The AssetBook datasource's record is being updated with the
    //  From and To LedgerDimension values entered on this datasource.  So clearing the DynaLinks to avoid this.
    this.query().dataSourceTable(tablenum(AssetBook)).clearDynalinks();
}

public Common lookupReference(FormReferenceControl _formReferenceControl)
{
    Set accountTypes;

    accountTypes = new Set(Types::Enum);
    accountTypes.add(DimensionLedgerAccountType::BalanceSheet);
    accountTypes.add(DimensionLedgerAccountType::Liability);
    accountTypes.add(DimensionLedgerAccountType::Asset);

    return MainAccount::lookupMainAccountByTypes(_formReferenceControl, accountTypes);
}

