public utcdatetime getDefaultEndDateForAudit(RefRecId _policyIndex, RefRecId _ruleTypeIndex)
{
    SysPolicyRule   ruleObject;
    utcdatetime     minDateValue = DateTimeUtil::minValue();
    utcdatetime     maxDateValue = DateTimeUtil::maxValue();

    select firstonly validtimestate(minDateValue, maxDateValue) ValidFrom, ValidTo from ruleObject
        order by ruleObject.ValidFrom
        where ruleObject.Policy == _policyIndex &&
              ruleObject.PolicyRuleType == _ruleTypeIndex &&
              ruleObject.ValidFrom > auditRule.ValidFrom;

    //If no row was found
    if (!ruleObject)
    {
        return DateTimeUtil::maxValue();
    }

    return DateTimeUtil::addSeconds(ruleObject.ValidFrom, -1);
}

public utcdatetime getDefaultStartDateForAudit(RefRecId _policyIndex, RefRecId _ruleTypeIndex)
{
    SysPolicyRule ruleObject;
    int counter;
    utcdatetime   startDate = DateTimeUtil::minValue();
    utcdatetime   endDate   = DateTimeUtil::utcNow();
    utcdatetime   minDateValue = DateTimeUtil::minValue();
    utcdatetime   maxDateValue = DateTimeUtil::maxValue();

    counter = 0;
    while select validtimestate( minDateValue, maxDateValue ) ValidFrom, ValidTo from ruleObject
        order by ruleObject.ValidTo
        where ruleObject.Policy == _policyIndex
            && ruleObject.PolicyRuleType == _ruleTypeIndex
            //need to add this as rows with same start and end dates are expired rules
            && ruleObject.ValidFrom != ruleObject.ValidTo
            && ruleObject.ValidTo >= DateTimeUtil::utcNow()
    {
        counter++;
        if ( ruleObject.ValidFrom < DateTimeUtil::addDays(DateTimeUtil::maxValue(), -1)
                    && ruleObject.ValidFrom > DateTimeUtil::addDays(enddate, 1))
        {
            break;
        }
        startdate = ruleObject.ValidFrom;
        enddate = ruleObject.ValidTo;
    }

    //If one active row starting in future, return now as start date
    if (counter == 1 && startdate >= DateTimeUtil::addDays(DateTimeUtil::utcNow(), 1))
    {
        return DateTimeUtil::utcNow();
    }

    if (enddate == DateTimeUtil::maxValue())
    {
        return DateTimeUtil::maxValue();
    }
    else if (enddate == DateTimeUtil::minValue())
    {
        return DateTimeUtil::utcNow();
    }
    else
    {
        return DateTimeUtil::addSeconds(enddate, 1);
    }
}

public void init()
{
    Query                       query = new Query();

    super();

    if (!element.args().caller())
    {
        throw error(Error::missingFormActiveBuffer(element.name()));
    }

    // Get the instance of the RunBase class the form was called from.
    auditPolicyRuleRunBase = element.args().caller().runBase();

    policyRuleTypeIndex = str2int64(auditPolicyRuleRunBase.getArgs().parm());
    sysPolicyRule = auditPolicyRuleRunBase.getArgs().record();
    policyIndex = sysPolicyRule.Policy;

    select firstonly QueryClassification from sourceDocumentRuleType
        where sourceDocumentRuleType.RecId == policyRuleTypeIndex;

    switch (sourceDocumentRuleType.QueryClassification)
    {
        case QueryClassification::Duplicate:
            supplementalInstruction.text("@SYS327969");
            DuplicateSinceDays.visible(true);
            break;
        case QueryClassification::KeywordSearch:
            supplementalInstruction.visible(false);
            queryGroup.caption("@SYS320615");
            rightButtonGrp.visible(false);
            dialogStartGrp.visible(false);
            keywordProhibitedWordsGroup.visible(true);
            keywordTableFieldsGroup.visible(true);
            tableFieldLookup = new SysTableFieldLookup(auditPolicyRuleRunBase.queryRun().query());
            break;
        case QueryClassification::ListSearch:
            supplementalInstruction.visible(false);
            queryGroup.caption('');
            queryGroup.frameType(1);
            rightButtonGrp.visible(false);
            dialogStartGrp.visible(false);
            ListGroup.visible(true);
            break;
        case QueryClassification::Sampling:
            supplementalInstruction.text("@SYS327971");
            SamplePercent.visible(true);
            break;
        case QueryClassification::Aggregate:
            supplementalInstruction.text("@SYS327968");
            break;
        case QueryClassification::Conditional:
            supplementalInstruction.text("@SYS327970");
            break;
    }

    if (sysPolicyRule)
    {
        SysPolicyUtil::filterPolicyRule(auditRule_ds, sysPolicyRule);
        editMode = true;
    }
    else
    {
        auditRule_DS.create();
    }
}

