/// <summary>
/// Validate the existence and validity of required supporting data.
/// </summary>
/// <returns>
/// true if required supporting data is valid, else false.
/// </returns>
public boolean validateSupportingData()
{
    boolean isValid = true;

    switch (sourceDocumentRuleType.QueryClassification)
    {
        case QueryClassification::KeywordSearch :
            // check that keyword table fields and keywords have been specified
            if (! AuditPolicyRuleDetail::existForPolicyRule(AuditPolicyRuleDetail.PolicySourceDocumentRule))
            {
                // this is informational only, so valid state is not changed
                info("@SYS4004716");
            }

            if (! AuditPolicyListKeyword::existsForPolicyRule(AuditPolicyListKeyword.SysPolicySourceDocumentRule))
            {
                // this is informational only, so valid state is not changed
                info("@SYS4004717");
            }

            break;

        case QueryClassification::ListSearch :
            // check that monitored entities have been specified
            if (! AuditPolicyListParty::existsForPolicyRule(AuditPolicyListParty.SysPolicySourceDocumentRule))
            {
                // this is informational only, so valid state is not changed
                info("@SYS4004718");
            }
            break;

        default :
        // currently there are no checks for other rule types
    }

    return isValid;
}

