public void initValue()
{
    super();
    auditRule.PolicyRuleType = policyRuleTypeIndex;
    auditRule.Policy = policyIndex;
    auditRule.ValidFrom = element.getDefaultStartDateForAudit(policyIndex, policyRuleTypeIndex);
    auditRule.ValidTo = element.getDefaultEndDateForAudit(policyIndex, policyRuleTypeIndex);
    auditRule.QueryPacked = auditPolicyRuleRunBase.queryRun().query().pack();

    auditRule.insert();
}

public boolean validateWrite()
{
    boolean ret;

    ret = super();

    if (AuditPolicyRuleDetail::existForPolicyRuleTableField(AuditPolicyRuleDetail.PolicySourceDocumentRule, AuditPolicyRuleDetail.SourceRelationType, AuditPolicyRuleDetail.SourceAttribute))
    {
        // the Table-Field combination already exists for the current policy rule, do not allow duplicates
        ret = ret && checkFailed(strFmt("@SYS153389", tablePName(AuditPolicyRuleDetail),
                strFmt("%1 - %2",       // table name - field name
                        tableId2pname(tableName2id(AuditPolicyRuleDetail.SourceRelationType)),
                        fieldId2pname(tableName2id(AuditPolicyRuleDetail.SourceRelationType),  fieldName2id(tableName2id(AuditPolicyRuleDetail.SourceRelationType), AuditPolicyRuleDetail.SourceAttribute)))));
    }

    return ret;
}

public void initValue()
