    auditPolicyRuleDetail.PolicySourceDocumentRule = auditRule.RecId;
}

public edit FieldLabel editFieldLabel(boolean _set, AuditPolicyRuleDetail _ruleDetail, FieldLabel _labelValue)
{
    DictField df;

    if (_set)
    {
        _ruleDetail.SourceAttribute = tableFieldLookup.parmSelectedRecord().FieldName;
    }

    df = new DictField(tableName2id(_ruleDetail.SourceRelationType),
                       fieldname2id(tableName2id(_ruleDetail.SourceRelationType), _ruleDetail.SourceAttribute));
    if (df)
    {
        _labelValue = df.label();
    }

    return _labelValue;
}

public edit TableLabel editTableLabel(boolean _set, AuditPolicyRuleDetail _ruleDetail, FieldLabel _labelValue)
{
    DictTable dt;

    if (_set)
    {
        _ruleDetail.SourceRelationType = tableId2name(tableFieldLookup.parmSelectedRecord().DictTableId);
    }

    dt = new DictTable(tableName2id(_ruleDetail.SourceRelationType));
    if (dt)
    {
        _labelValue = dt.label();
    }

    return _labelValue;
}

public boolean validateWrite()
{
    boolean ret;

    ret = super();

    if (AuditPolicyListKeyword::existsForPolicyRuleKeyword(AuditPolicyListKeyword.SysPolicySourceDocumentRule, AuditPolicyListKeyword.Keyword))
    {
        // the keyword already exists for the current policy rule, do not allow duplicates
        ret = ret && checkFailed(strFmt("@SYS153389", tablePName(AuditPolicyListKeyword), AuditPolicyListKeyword.Keyword));
    }

    return ret;
}

